                sphere(d = body_width * 0.8);
            translate([0, 0, body_height * 0.1])
                sphere(d = body_width * 0.9);
        }

        // Hollow out for 3D printing
        translate([0, 0, body_height * 0.5])
            scale([0.7, 0.7, 0.9])
            hull() {
                translate([0, 0, body_height * 0.4])
                    sphere(d = body_width * 0.7);
