            translate([0, -35, 55]) {
                color(color_beak)
                rotate([30, 0, 0])
                scale([0.3, 1, 0.3])
                cylinder(h = 25, r1 = 5, r2 = 1);
            }

            // Beak tip
            translate([0, -45, 60]) {
                color(color_beak)
                rotate([30, 0, 0])
                scale([0.3, 0.5, 0.3])
                cylinder(h = 8, r1 = 1, r2 = 0.5);
            }

            // Red crest
            translate([0, -15, 67]) {
                color(color_red)
                rotate([0, 0, 0])
                scale([0.2, 2, 0.1])
                cylinder(h = 15, r1 = 5, r2 = 0.5);
            }

            // Eyes
            translate([4, -15, 58]) {
                color(color_black)
                sphere(r = 2);
            }
            translate([-4, -15, 58]) {
                color(color_black)
                sphere(r = 2);
            }

            // Eye highlights
            translate([4.5, -15.5, 59]) {
                color(color_white)
                sphere(r = 0.7);
            }
            translate([-3.5, -15.5, 59]) {
                color(color_white)
                sphere(r = 0.7);
            }

            // Wings
            module wing(side) {
                translate([side * 20, 0, 30]) {
                    rotate([0, 0, side * 20]) {
                        color(color_brown)
                        difference() {
                            union() {
                                // Main wing
                                translate([side * 5, 0, 0]) {
                                    scale([1.8, 0.3, 0.8])
                                    rotate([0, 45, 0])
