color_spike = [0.85, 0.85, 0.85];   // Light gray
color_nose = [0.7, 0.5, 0.3];       // Brown
color_eye = [0.2, 0.2, 0.2];        // Black
color_belly = [0.9, 0.8, 0.7];      // Light tan

// Main body module
module sandslash() {
    color(color_body) {
        // Torso - egg shape
        scale([1, 0.8, 1.2]) sphere(15, $fn=32);

        // Belly
        translate([0, -11, -5]) 
        color(color_belly)
        scale([1.2, 0.3, 1]) sphere(12, $fn=32);
    }

    // Head
    translate([0, 18, 12]) {
        color(color_body) sphere(10, $fn=32);

        // Snout
        translate([0, 8, -2]) {
            color(color_body) rotate([0, -20, 0]) scale([0.8, 1, 1.2]) sphere(6, $fn=32);
            // Nose
            translate([0, 6, 0]) color(color_nose) sphere(2, $fn=16);
        }

        // Ears
        for(side = [-1, 1]) {
            translate([side * 6, 2, 6]) {
                color(color_body) rotate([30, side * 20, 0]) cylinder(8, 3, 2, $fn=16);
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([side * 5, 5, 3]) {
                color(color_eye) sphere(2, $fn=16);
            }
        }
    }

    // Arms
    for(side = [-1, 1]) {
        translate([side * 12, 8, 0]) {
            rotate([0, 0, side * 45]) {
                color(color_body) cylinder(10, 5, 4, $fn=16);
                // Claws
                translate([0, 10, 0]) {
                    rotate([0, 90, 0]) {
                        for(i = [-1, 0, 1]) {
                            translate([i * 2, 0, 0]) {
                                color(color_spike) rotate([0, 0, 45]) scale([0.5, 0.5, 2]) cylinder(6, 2, 0, $fn=8);
                            }
                        }
                    }
                }
            }
        }
    }

    // Legs
    for(side = [-1, 1]) {
        translate([side * 8, -8, -12]) {
            color(color_body) cylinder(12, 5, 4, $fn=16);
            // Feet
            translate([0, -12, 2]) {
                color(color_body) scale([1.5, 1, 0.5]) sphere(5, $fn=16);
            }
        }
    }

    // Tail segments
    translate([0, -15, 0]) {
        for(i = [1:4]) {
            color(color_body) 
            translate([0, -i * 8, -i * 2]) 
            scale([1 - i * 0.15, 1 - i * 0.15, 1]) 
            sphere(8 - i, $fn=32);
        }
    }

    // Back spikes
    module spike() {
        color(color_spike) 
        scale([0.8, 0.8, 1]) 
        cylinder(8, 3, 0, $fn=8);
    }

    // Back spikes pattern
    for(x = [-8:8:8]) {
        for(z = [-5:5:10]) {
            translate([x, 0, z]) {
                rotate([30, 0, 0]) spike();
            }
        }
    }

    // Head spikes
    for(angle = [0:60:300]) {
        translate([0, 18, 12]) {
            rotate([0, 0, angle]) {
                translate([10, 0, 0]) {
                    rotate([45, 0, 0]) spike();
                }
            }
        }
    }

    // Tail spikes
    for(i = [1:4]) {
        for(angle = [0:90:270]) {
            translate([0, -15 - i * 8, -i * 2]) {
                rotate([0, 0, angle]) {
                    translate([8 - i, 0, 0]) {
                        rotate([30, 0, 0]) spike();
                    }
                }
            }
        }
    }
}

