// Realistic Zubat OpenSCAD Model
// Medium complexity, 3D printable

// Overall scale factor
scale_factor = 0.4;

// Main color definitions
color_body = [0.2, 0.2, 0.25];
color_wings = [0.7, 0.7, 0.9];
color_ears = [0.8, 0.3, 0.3];
color_eyes = [0.9, 0.1, 0.1];
color_teeth = [0.95, 0.95, 0.9];

module zubat_body() {
    color(color_body)
    union() {
        // Main body sphere
        translate([0, 0, 0])
            scale([1, 1.2, 0.8])
            sphere(d=30 * scale_factor, $fn=32);

        // Bottom point
        translate([0, 0, -12 * scale_factor])
            rotate([0, 180, 0])
            cylinder(h=15 * scale_factor, r1=8 * scale_factor, r2=0, $fn=16);
    }
}

module zubat_wings() {
    color(color_wings)
    union() {
        // Left wing
        translate([-15 * scale_factor, 0, 5 * scale_factor])
            rotate([0, 0, 30])
            difference() {
                // Wing membrane
                scale([2.5, 1.5, 0.1])
                    sphere(d=25 * scale_factor, $fn=32);

                // Wing holes
                for(i = [0:2]) {
                    translate([i * 8 * scale_factor - 8 * scale_factor, 0, -1 * scale_factor])
                        scale([0.3, 0.4, 1])
                        cylinder(h=3 * scale_factor, d=5 * scale_factor, $fn=8);
                }
            }

        // Right wing (mirrored)
        mirror([1, 0, 0])
            translate([-15 * scale_factor, 0, 5 * scale_factor])
                rotate([0, 0, 30])
                difference() {
                    scale([2.5, 1.5, 0.1])
                        sphere(d=25 * scale_factor, $fn=32);

                    for(i = [0:2]) {
                        translate([i * 8 * scale_factor - 8 * scale_factor, 0, -1 * scale_factor])
                            scale([0.3, 0.4, 1])
                            cylinder(h=3 * scale_factor, d=5 * scale_factor, $fn=8);
                    }
                }
    }
}

module zubat_ears() {
    color(color_ears)
    union() {
        // Left ear
        translate([-8 * scale_factor, 12 * scale_factor, 8 * scale_factor])
            rotate([0, -20, -45])
            scale([0.3, 0.3, 2])
            cylinder(h=20 * scale_factor, r1=5 * scale_factor, r2=2 * scale_factor, $fn=8);

        // Right ear
        translate([8 * scale_factor, 12 * scale_factor, 8 * scale_factor])
            rotate([0, -20, 45])
            scale([0.3, 0.3, 2])
            cylinder(h=20 * scale_factor, r1=5 * scale_factor, r2=2 * scale_factor, $fn=8);
    }
}

module zubat_face() {
    union() {
        // Eyes
        color(color_eyes)
        union() {
            // Left eye
            translate([-5 * scale_factor, 8 * scale_factor, 10 * scale_factor])
                sphere(d=6 * scale_factor, $fn=16);

            // Right eye
            translate([5 * scale_factor, 8 * scale_factor, 10 * scale_factor])
                sphere(d=6 * scale_factor, $fn=16);
        }

        // Mouth with teeth
        color(color_body)
        translate([0, 5 * scale_factor, 8 * scale_factor])
            rotate([90, 0, 0])
            cylinder(h=8 * scale_factor, r1=4 * scale_factor, r2=2 * scale_factor, $fn=16);

        // Teeth
        color(color_teeth)
        for(i = [-1:1]) {
            translate([i * 2 * scale_factor, 5 * scale_factor, 12 * scale_factor])
                rotate([90, 0, 0])
                cylinder(h=3 * scale_factor, r1=1 * scale_factor, r2=0.5 * scale_factor, $fn=8);
        }
    }
}

module zubat_feet() {
    color(color_body)
    union() {
        // Left foot
        translate([-5 * scale_factor, -5 * scale_factor, -10 * scale_factor])
            rotate([0, 20, 0])
            scale([0.5, 0.5, 1])
            cylinder(h=8 * scale_factor, r1=3 * scale_factor, r2=2 * scale_factor, $fn=8);

        // Right foot
        translate([5 * scale_factor, -5 * scale_factor, -10 * scale_factor])
            rotate([0, -20, 0])
            scale([0.5, 0.5, 1])
            cylinder(h=8 * scale_factor, r1=3 * scale_factor, r2=2 * scale_factor, $fn=8);
    }
}

// Assemble the complete Zubat
union() {
    zubat_body();
    zubat_wings();
    zubat_ears();
    zubat_face();
    zubat_feet();
}

// Golbat - Realistic style, medium complexity (OpenSCAD)

// Dimensions in mm
body_r = 25;
wing_span = 90;
ear_len = 20;
leg_len = 8;
eye_r = 6;
mouth_w = 18;
mouth_h = 14;

// Colors
color_purple = [0.4, 0.2, 0.6];
color_blue = [0.2, 0.3, 0.7];
color_red = [0.8, 0.1, 0.1];
color_white = [0.9, 0.9, 0.9];
color_black = [0.1, 0.1, 0.1];

module golbat() {
    union() {
        // Main spherical body
        color(color_purple)
        translate([0, 0, body_r/2])
        sphere(r = body_r, $fn = 32);

        // Ears (two pointy cones)
        color(color_purple)
        union() {
            // Left ear
            translate([-body_r*0.5, 0, body_r*1.3])
            rotate([0, -20, -30])
            cylinder(h = ear_len, r1 = 5, r2 = 1, $fn = 8);

            // Right ear
            translate([body_r*0.5, 0, body_r*1.3])
            rotate([0, -20, 30])
            cylinder(h = ear_len, r1 = 5, r2 = 1, $fn = 8);
        }

        // Wings (membranous with veins)
        color(color_purple) {
            // Left wing
            translate([-body_r*0.8, 0, body_r*0.8])
            rotate([0, 0, -20])
            scale([1, 0.1, 1])
            sphere(r = wing_span/2, $fn = 32);

            // Right wing
            translate([body_r*0.8, 0, body_r*0.8])
            rotate([0, 0, 20])
            scale([1, 0.1, 1])
            sphere(r = wing_span/2, $fn = 32);
        }

        // Wing membrane details (veins)
        color(color_blue) {
            for(i = [-30:15:-60]) {
                // Left wing veins
                translate([-body_r*0.8, 0, body_r*0.8])
                rotate([0, 0, i])
                scale([1, 0.05, 0.8])
                cylinder(h = wing_span/2, r1 = 0.5, r2 = 0.2, $fn = 4);

                // Right wing veins
                translate([body_r*0.8, 0, body_r*0.8])
                rotate([0, 0, -i])
                scale([1, 0.05, 0.8])
                cylinder(h = wing_span/2, r1 = 0.5, r2 = 0.2, $fn = 4);
            }
        }

        // Eyes (large red with black pupils)
        color(color_red)
        union() {
            translate([-body_r*0.5, -body_r*0.8, body_r*0.7])
            sphere(r = eye_r, $fn = 16);

            translate([body_r*0.5, -body_r*0.8, body_r*0.7])
            sphere(r = eye_r, $fn = 16);
        }

        // Pupils
        color(color_black)
        union() {
            translate([-body_r*0.5, -body_r*0.9, body_r*0.75])
            sphere(r = eye_r/2, $fn = 8);

            translate([body_r*0.5, -body_r*0.9, body_r*0.75])
            sphere(r = eye_r/2, $fn = 8);
        }

        // Mouth (large fanged maw)
        color(color_red)
        difference() {
            // Main mouth opening
            translate([0, -body_r*0.9, body_r*0.3])
            rotate([90, 0, 0])
            cylinder(h = body_r*0.5, r1 = mouth_w/2, r2 = mouth_w/2, $fn = 16);

            // Hollow out mouth
            translate([0, -body_r*1.1, body_r*0.3])
            rotate([90, 0, 0])
            cylinder(h = body_r*0.6, r1 = mouth_w/2-2, r2 = mouth_w/2-2, $fn = 16);
        }

        // Teeth (top and bottom fangs)
        color(color_white)
        union() {
            // Top fangs
            translate([-mouth_w/4, -body_r*1.1, body_r*0.4])
            rotate([0, 30, 0])
            cylinder(h = 8, r1 = 2, r2 = 0.5, $fn = 8);

            translate([mouth_w/4, -body_r*1.1, body_r*0.4])
            rotate([0, 30, 0])
            cylinder(h = 8, r1 = 2, r2 = 0.5, $fn = 8);

            // Bottom fangs
            translate([-mouth_w/4, -body_r*1.1, body_r*0.2])
            rotate([0, -30, 0])
            cylinder(h = 8, r1 = 2, r2 = 0.5, $fn = 8);

            translate([mouth_w/4, -body_r*1.1, body_r*0.2])
            rotate([0, -30, 0])
            cylinder(h = 8, r1 = 2, r2 = 0.5, $fn = 8);
        }

        // Legs (small curved claws)
        color(color_purple)
        union() {
            // Left leg
            translate([-body_r*0.4, 0, 0])
            rotate([0, 20, -20])
            cylinder(h = leg_len, r1 = 2, r2 = 1, $fn = 8);

            // Right leg
            translate([body_r*0.4, 0, 0])
            rotate([0, 20, 20])
            cylinder(h = leg_len, r1 = 2, r2 = 1, $fn = 8);
        }

        // Feet (small spheres at end of legs)
        color(color_purple)
        union() {
            translate([-body_r*0.4, 0, -leg_len])
            sphere(r = 2, $fn = 8);

            translate([body_r*0.4, 0, -leg_len])
            sphere(r = 2, $fn = 8);
        }

        // Tail (small pointed tail)
        color(color_purple)
        translate([0, body_r*0.7, -body_r*0.3])
        rotate([0, 0, 180])
        cylinder(h = 10, r1 = 4, r2 = 1, $fn = 8);
    }
}

