    color(white) sphere(1.5);
    color(black) translate([0.5, 0, 0.5]) sphere(0.8);
}

// Mushroom cap segment
module cap_segment(radius, height, angle) {
    rotate([0, 0, angle]) {
        translate([radius, 0, height]) rotate([0, 90, 0]) 
            cylinder(h=radius*0.8, r1=radius*0.3, r2=0, $fn=8);
    }
