// Diglett - Realistic Style, Medium Complexity
// Dimensions: ~70mm height for 3D printing

// Constants
module_size = 1; // Scale factor for easy resizing
body_height = 70 * module_size;
body_radius = 35 * module_size;
head_radius = 40 * module_size;
nose_length = 15 * module_size;
nose_radius = 8 * module_size;
eye_size = 6 * module_size;
mouth_width = 20 * module_size;
mouth_height = 8 * module_size;
stone_size = 12 * module_size;
earth_base_height = 10 * module_size;

// Color definitions
color_body = "#8B4513";
color_nose = "#D2691E";
color_eyes = "#FF0000";
color_stones = "#696969";
color_earth = "#654321";

// Main module
module diglett() {
    // Earth base
    color(color_earth)
    difference() {
        cylinder(h=earth_base_height, r1=body_radius*1.5, r2=body_radius*1.4, $fn=32);
        // Hollow base for 3D printing
        cylinder(h=earth_base_height+1, r1=body_radius*1.4, r2=body_radius*1.3, $fn=32);
    }

    // Main body with realistic curves
    color(color_body)
    union() {
        // Upper rounded head
        translate([0, 0, earth_base_height + body_height/3])
        sphere(r=head_radius, $fn=64);

        // Tapering body
        color(color_body)
        hull() {
            translate([0, 0, earth_base_height])
            sphere(r=body_radius*0.9, $fn=32);

            translate([0, 0, earth_base_height + body_height/2])
            sphere(r=body_radius*0.7, $fn=32);

            translate([0, 0, earth_base_height + body_height])
            sphere(r=body_radius*0.3, $fn=32);
        }

        // Large nose
        color(color_nose)
        translate([0, -head_radius*0.8, earth_base_height + body_height/2])
        rotate([20, 0, 0])
        cylinder(h=nose_length, r1=nose_radius, r2=nose_radius*0.6, $fn=32);

        // Eyes
        color(color_eyes)
        for(x = [-1, 1]) {
            translate([x * head_radius/2, -head_radius*0.6, earth_base_height + body_height/1.5])
            sphere(r=eye_size, $fn=16);
        }

        // Mouth
        color("#2F1B14")
        translate([0, -head_radius*0.4, earth_base_height + body_height/1.8])
        difference() {
            cube([mouth_width, mouth_height*2, 5], center=true);
            translate([0, 0, 2])
            cube([mouth_width*0.8, mouth_height, 6], center=true);
        }

        // Rocks/stones on head for realism
        color(color_stones)
        for(i = [0:5]) {
            rotate([0, 0, i*60 + 30])
            translate([head_radius*0.9, 0, earth_base_height + body_height*0.9])
            scale([1, 0.7, 0.5])
            sphere(r=stone_size, $fn=8);
        }

        // Whisker-like markings
        color("#4A2C17")
        for(angle = [-45, 45]) {
            rotate([0, 0, angle])
            translate([head_radius*0.6, -head_radius*0.5, earth_base_height + body_height/1.4])
            scale([0.2, 0.2, 1])
            cylinder(h=head_radius*0.3, r=eye_size/2, $fn=8);
        }

        // Body texture grooves
        for(level = [1:3]) {
            color("#654321")
            translate([0, 0, earth_base_height + level*body_height/4])
            rotate_extrude($fn=64)
            translate([body_radius*0.8 - level*5, 0, 0])
            circle(r=1.5, $fn=8);
        }
    }
}

