mouth_color = [0.6, 0.1, 0.1];

module poliwag_body() {
    // Main spherical body
    color(body_color)
    translate([0, 0, 25])
    sphere(d=40, $fn=32);

    // Flattened bottom for stability
    color(body_color)
    translate([0, 0, 5])
    scale([1, 1, 0.2])
    sphere(d=40, $fn=32);

    // White belly patch
    color(belly_color)
    translate([0, -19, 15])
    scale([0.8, 0.3, 0.8])
    sphere(d=40, $fn=32);
}

module poliwag_tail() {
    // Spiral tail segments
    for(i = [0:3]) {
        color(body_color)
        rotate([0, 0, i * 90])
        translate([20 + i * 3, 0, 15 - i * 5])
        sphere(d=12 - i * 2, $fn=16);
    }

    // Tail stem
    color(body_color)
    translate([32, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=35, d1=8, d2=5, $fn=16);

    // Tail fins
    color(body_color)
    translate([50, 0, 0])
    rotate([0, 90, 0])
    scale([1, 0.3, 1])
    sphere(d=20, $fn=16);
}

module poliwag_lips() {
    color(body_color)
    difference() {
        // Upper lip
        translate([0, 20, 15])
        scale([1, 0.4, 0.6])
        sphere(d=25, $fn=32);

        // Mouth cavity
        color(mouth_color)
        translate([0, 21, 14])
        scale([0.6, 0.2, 0.4])
        sphere(d=20, $fn=32);
    }

    color(body_color)
    difference() {
        // Lower lip
        translate([0, 20, 8])
        scale([1, 0.4, 0.4])
        sphere(d=25, $fn=32);

        // Mouth cavity continuation
        color(mouth_color)
        translate([0, 21, 9])
        scale([0.6, 0.2, 0.3])
        sphere(d=20, $fn=32);
    }
}

module poliwag_eyes() {
    // Left eye
    color(eye_color)
    translate([-10, 10, 35])
    scale([1, 0.8, 0.5])
    sphere(d=8, $fn=16);

    // Right eye
    color(eye_color)
    translate([10, 10, 35])
    scale([1, 0.8, 0.5])
    sphere(d=8, $fn=16);

    // Eye highlights
    color([1, 1, 1])
    translate([-9, 11, 37])
    sphere(d=2, $fn=8);

    color([1, 1, 1])
    translate([11, 11, 37])
    sphere(d=2, $fn=8);
}

module poliwag_legs() {
    // Front legs
    for(side = [-1, 1]) {
        color(body_color)
        translate([side * 12, 15, 5])
        rotate([0, 45, 0])
        cylinder(h=15, d1=8, d2=6, $fn=16);

        color(body_color)
        translate([side * 12, 25, -2])
        sphere(d=10, $fn=16);
    }

    // Back leg
    color(body_color)
    translate([0, -15, 5])
    rotate([0, -45, 0])
    cylinder(h=15, d1=8, d2=6, $fn=16);

    color(body_color)
    translate([0, -25, -2])
    sphere(d=10, $fn=16);
}

module poliwag_whiskers() {
    // Left whiskers
    for(i = [1, 2]) {
        color(body_color)
        translate([-15, 5, 22])
        rotate([0, 45 + i * 10, -30 * i])
        cylinder(h=12, d=2, $fn=8);
    }

    // Right whiskers
    for(i = [1, 2]) {
        color(body_color)
        translate([15, 5, 22])
        rotate([0, 45 + i * 10, 30 * i])
        cylinder(h=12, d=2, $fn=8);
    }
}

// Assembly
union() {
    poliwag_body();
    poliwag_tail();
    poliwag_lips();
    poliwag_eyes();
    poliwag_legs();
    poliwag_whiskers();
