// Slowbro Pokemon - Medium Complexity Realistic Style
// 3D printable model with proper wall thickness

// Color definitions
pink = [0.95, 0.75, 0.85];
beige = [0.95, 0.85, 0.75];
off_white = [0.98, 0.95, 0.90];
gray = [0.6, 0.6, 0.65];
black = [0.1, 0.1, 0.1];
red = [0.85, 0.2, 0.2];

// Global scale for 3D printing
scale_factor = 0.8;

module slowbro_body() {
    color(pink)
    union() {
        // Main body - rounded torso
        translate([0, 0, 15])
            scale([1.2, 1, 1.1])
            sphere(15, $fn=32);

        // Belly
        translate([0, -10, 12])
            scale([1.1, 0.8, 0.9])
            sphere(12, $fn=32);
    }
}

module shell_on_back() {
    color(gray)
    union() {
        // Main spiral shell
        translate([-12, 8, 15])
            rotate([0, 15, 0])
            scale([1.3, 1.3, 0.8])
            sphere(12, $fn=32);

        // Shell spiral detail
        for(i=[0:30:330]) {
            rotate([0, 0, i])
            translate([-18, 12, 18])
                scale([0.3, 0.3, 1.2])
                cylinder(h=2, r=3, $fn=16);
        }

        // Shell opening
        difference() {
            translate([-15, 12, 18])
                sphere(8, $fn=32);
            translate([-15, 12, 18])
                sphere(6, $fn=32);
        }
    }
}

module head() {
    color(pink)
    union() {
        // Main head sphere
        translate([0, -20, 25])
            scale([0.9, 1.1, 1])
            sphere(12, $fn=32);

        // Snout
        translate([0, -30, 25])
            scale([0.7, 0.8, 0.6])
            sphere(8, $fn=32);

        // Eyes
        color(off_white)
        translate([-5, -25, 28])
            sphere(3, $fn=16);
        translate([5, -25, 28])
            sphere(3, $fn=16);

        // Pupils
        color(black)
        translate([-5, -26, 29])
            sphere(1.5, $fn=16);
        translate([5, -26, 29])
            sphere(1.5, $fn=16);

        // Mouth
        color(red)
        translate([0, -33, 24])
            scale([1.2, 0.5, 0.5])
            sphere(2, $fn=16);
    }
}

module ears() {
    color(pink)
    union() {
        // Left ear
        translate([-8, -22, 35])
            rotate([30, 0, -20])
            scale([0.5, 2, 0.3])
            cylinder(h=8, r=3, $fn=16);

        // Right ear
        translate([8, -22, 35])
            rotate([30, 0, 20])
            scale([0.5, 2, 0.3])
            cylinder(h=8, r=3, $fn=16);
    }
}

module arms() {
    color(pink)
    union() {
        // Left arm
        translate([-15, -5, 15])
            rotate([0, 0, -30])
            scale([0.8, 3, 0.8])
            cylinder(h=8, r=3, $fn=16);

        // Right arm
        translate([15, -5, 15])
            rotate([0, 0, 30])
            scale([0.8, 3, 0.8])
            cylinder(h=8, r=3, $fn=16);

        // Hands
        translate([-18, -8, 15])
            sphere(4, $fn=16);
        translate([18, -8, 15])
            sphere(4, $fn=16);
    }
}

module legs() {
    color(pink)
    union() {
        // Left leg
        translate([-8, 0, 0])
            scale([0.8, 0.8, 1.2])
            cylinder(h=10, r=4, $fn=16);

        // Right leg
        translate([8, 0, 0])
            scale([0.8, 0.8, 1.2])
            cylinder(h=10, r=4, $fn=16);

        // Feet
        translate([-8, 0, 0])
            scale([1.2, 2, 0.5])
            sphere(4, $fn=16);
        translate([8, 0, 0])
            scale([1.2, 2, 0.5])
            sphere(4, $fn=16);
    }
}

module tail() {
    color(pink)
    union() {
        // Main tail
        translate([0, 12, 12])
            rotate([0, 20, 0])
            scale([0.8, 3, 0.8])
            cylinder(h=15, r=3, $fn=16);

        // Tail tip - Shellder
        color(purple = [0.6, 0.4, 0.7])
        translate([0, 25, 15])
            scale([1.2, 1.2, 1])
            sphere(5, $fn=32);

        // Shellder spike
        color(gray)
        translate([0, 30, 15])
            rotate([0, 0, 0])
            scale([0.3, 1, 0.3])
            cylinder(h=4, r=2, $fn=8);
    }
}

