// Color definitions
$fn = 32;

// Main body module
module farfetchd() {
    union() {
        // Body (brown duck body)
        color([0.5, 0.35, 0.2]) {
            // Torso - elongated oval
            translate([0, 0, 20])
                scale([1.2, 0.8, 1.3])
                sphere(r=15);

            // Belly (lighter brown)
            color([0.65, 0.45, 0.3]) {
                translate([0, -12, 18])
                    scale([0.8, 0.5, 0.8])
                    sphere(r=12);
            }
        }

        // Head
        color([0.5, 0.35, 0.2]) {
            translate([0, 0, 45])
                sphere(r=12);

            // Beak
            color([0.9, 0.7, 0.2]) {
                translate([0, 12, 42])
                    rotate([30, 0, 0])
                    scale([0.8, 2, 0.8])
                    cylinder(h=10, r1=5, r2=0);
            }

            // Eyes
            color([0, 0, 0]) {
                translate([-4, 8, 48]) sphere(r=1.5);
                translate([4, 8, 48]) sphere(r=1.5);
            }

            // Eyebrows
            color([0.3, 0.2, 0.1]) {
                translate([-6, 12, 50])
                    rotate([0, 0, 30])
                    scale([0.3, 2, 0.3])
                    cylinder(h=3, r=1);
                translate([6, 12, 50])
                    rotate([0, 0, -30])
                    scale([0.3, 2, 0.3])
                    cylinder(h=3, r=1);
            }
        }

        // Wings
        module wing(side) {
            translate([side * 15, 0, 35]) {
                rotate([0, 0, side * -20]) {
                    color([0.5, 0.35, 0.2]) {
                        scale([0.3, 1.5, 0.8])
                            rotate([0, 30, 0])
                            cylinder(h=20, r1=8, r2=5);
                    }
                }
            }
        }
        wing(1);
        wing(-1);

        // Legs
        module leg(side) {
            translate([side * 8, -5, 15]) {
                color([0.9, 0.7, 0.2]) {
                    // Upper leg
                    cylinder(h=15, r=3);

                    // Foot
                    translate([0, 0, -8])
                        rotate([0, 90, 0])
                        scale([1.5, 0.5, 0.8])
                        cylinder(h=8, r=3);
                }
            }
        }
        leg(1);
        leg(-1);

        // Leek (signature item)
        color([0.2, 0.8, 0.2]) {
            translate([20, 15, 30]) {
                rotate([0, 0, -45]) {
                    // Leek stalk
                    cylinder(h=35, r1=2, r2=1.5);

                    // Leek leaves
                    translate([0, 0, 35])
                        rotate([30, 0, 0])
                        scale([1, 0.2, 3])
                        sphere(r=4);

                    // Leek tip
                    translate([0, 0, -5])
                        cylinder(h=5, r1=1.5, r2=0);
                }
            }
        }

        // Tail feathers
        color([0.5, 0.35, 0.2]) {
            translate([0, -15, 25]) {
                rotate([30, 0, 0]) {
                    // Main tail
                    scale([0.3, 2, 0.8])
                        cylinder(h=12, r1=4, r2=2);

                    // Side tail feathers
                    translate([0, 6, 2])
                        rotate([0, 15, 0])
                        scale([0.2, 1.5, 0.6])
                        cylinder(h=10, r1=3, r2=1);

                    translate([0, -6, 2])
                        rotate([0, -15, 0])
                        scale([0.2, 1.5, 0.6])
                        cylinder(h=10, r1=3, r2=1);
                }
            }
        }
    }
}

