scale_factor = 0.4;

// Main colors
color_body = [139/255, 69/255, 19/255];    // Brown
color_belly = [245/255, 222/255, 179/255]; // Beige
color_neck = [160/255, 82/255, 45/255];    // Darker brown
color_beak = [255/255, 215/255, 0/255];    // Gold
color_eyes = [0/255, 0/255, 0/255];        // Black
color_feet = [255/255, 140/255, 0/255];    // Orange

// Body module
module body() {
    color(color_body)
    union() {
        // Main body - egg shaped
        scale([1, 0.8, 1.2])
            sphere(r=15);

        // Belly patch
        color(color_belly)
        translate([0, -10, -10])
            scale([1, 0.5, 0.3])
                sphere(r=12);
    }
}

// Neck module (reusable for all three necks)
module neck(height = 30, angle = 0, lean = 0) {
    color(color_neck)
    rotate([0, lean, angle])
    translate([0, 0, height/2])
        scale([0.5, 0.5, 1])
            cylinder(h=height, r1=6, r2=4, $fn=8);
}

// Head module
module head() {
    color(color_body)
    union() {
        // Main head shape
        sphere(r=8);

        // Beak
        color(color_beak)
        translate([0, 0, -8])
            rotate([0, 20, 0])
                scale([1, 0.3, 2])
                    cylinder(h=12, r1=4, r2=0, $fn=6);

        // Eyes
        color(color_eyes)
        for(side = [-1, 1]) {
            translate([side * 4, 3, 3])
                sphere(r=1.5);
        }

        // Feather crest
        color(color_body)
        translate([0, 0, 8])
            rotate([0, 0, 0])
                scale([0.2, 1, 2])
                    cylinder(h=6, r1=3, r2=0, $fn=4);
    }
}

// Leg module
module leg(x_pos = 0, y_pos = 0) {
    color(color_feet)
    union() {
        // Upper leg
        color(color_body)
        translate([x_pos, y_pos, -20])
            rotate([0, 0, 0])
                cylinder(h=15, r=2.5, $fn=8);

        // Lower leg
        translate([x_pos, y_pos, -35])
            cylinder(h=12, r=2, $fn=8);

        // Foot
        translate([x_pos, y_pos + 3, -47])
            scale([2, 1, 0.5])
                sphere(r=3);
    }
}

// Tail feathers
module tail() {
    color(color_body)
    for(i = [0, 120, 240]) {
        rotate([0, 0, i])
        translate([-18, 0, -5])
            rotate([30, 0, 0])
                scale([0.5, 2, 0.1])
                    cylinder(h=10, r1=2, r2=0, $fn=4);
    }
}

