            rotate([0, 0, 0])
            scale([0.3, 0.3, 1])
                cylinder(h=15, d1=8, d2=3);
    }

    // Inner pearl
    color(pearl_color)
    translate([0, 0, -10])
    difference() {
        sphere(d=35);
        translate([0, 0, 15])
            cube([50, 50, 30], center=true);
    }

