color_red = [0.8, 0.1, 0.1];
color_white = [0.9, 0.9, 0.9];
color_black = [0.1, 0.1, 0.1];

// Main gas cloud body
module gastly_body() {
    color(color_purple) {
        // Central sphere
        sphere(d = 50);

        // Gas wisps - using difference to create cloud-like shapes
        for(i = [0:5]) {
            rotate([i*60, 0, 0]) 
                translate([0, 25 + i*3, 0]) 
                    sphere(d = 20 - i*2);
        }

        for(i = [0:4]) {
            rotate([0, i*45, 0]) 
                translate([25 + i*2, 0, 0]) 
                    sphere(d = 18 - i*2);
        }
    }
}

// Poison gas wisps
module poison_wisps() {
    color(color_dark_purple) {
        for(i = [0:7]) {
            rotate([i*51.4, i*20, 0]) {
                translate([30 + sin(i*45)*5, 0, cos(i*45)*5]) {
                    scale([1, 0.3, 0.3]) 
                        sphere(d = 8);
                }
            }
        }
    }
}

// Main eye structure
module eye() {
    // Eye socket
    difference() {
        color(color_white) 
            sphere(d = 12);

        // Pupil hole
        translate([0, 0, 5.9]) 
            color(color_black) 
                cylinder(h = 2, d = 8, $fn = 32);

        // Red iris
        translate([0, 0, 5.8]) 
            color(color_red) 
                cylinder(h = 1, d = 10, $fn = 32);
    }
}

// Small eye structure
module small_eye() {
    difference() {
        color(color_white) 
            sphere(d = 8);

        translate([0, 0, 3.9]) 
            color(color_black) 
                cylinder(h = 2, d = 5, $fn = 32);

        translate([0, 0, 3.8]) 
            color(color_red) 
                cylinder(h = 1, d = 6.5, $fn = 32);
    }
}

// Mouth with teeth
module mouth() {
    difference() {
        // Main mouth shape
        color(color_dark_purple) 
            scale([1, 0.6, 0.3]) 
                sphere(d = 20);

        // Mouth cavity
        translate([0, -3, 2]) 
            scale([0.8, 0.4, 0.8]) 
                sphere(d = 18);

        // Teeth
        for(i = [0:4]) {
            rotate([0, 0, -30 + i*15]) 
                translate([6 - i*2.5, -4, 3.5]) 
                    cube([1.5, 3, 1.5], center = true);
        }

        for(i = [0:3]) {
            rotate([0, 0, -22.5 + i*15]) 
                translate([5.5 - i*2.5, -4, 3.5]) 
                    cube([1.5, 2.5, 1.5], center = true);
        }
    }
}

// Poison gas bubbles
