    color(color_dark_purple) {
        for(i = [0:12]) {
            rotate([i*30, i*20, 0]) 
                translate([25 + sin(i*45)*8, 10 + cos(i*30)*5, sin(i*60)*8]) 
                    sphere(d = 3 + i*0.2);
        }
    }
}

