    gastly_body();
    poison_wisps();

    // Eyes
    translate([-12, 8, 20]) 
        rotate([15, 0, 0]) 
            eye();

    translate([12, 8, 20]) 
        rotate([15, 0, 0]) 
            mirror([1, 0, 0]) 
                small_eye();

    // Mouth
    translate([0, -8, 15]) 
        rotate([25, 0, 0]) 
            mouth();

    // Gas bubbles
    bubbles();
