eye_color = [0.1, 0.1, 0.1];
accent_color = [0.7, 0.6, 0.5];

// Main parameters
scale_factor = 0.5;
body_segments = 8;
segment_length = 8 * scale_factor;
segment_height = 6 * scale_factor;
head_size = 10 * scale_factor;

module onix_head() {
    // Main head rock
    color(rock_color)
    translate([0, 0, head_size/2])
    scale([1.2, 0.8, 1])
    sphere(head_size);

    // Snout
    color(rock_color)
    translate([head_size*0.8, 0, -head_size*0.2])
    scale([1.5, 0.7, 0.6])
    sphere(head_size*0.6);

    // Eyes
    color(eye_color)
    for(i = [-1, 1]) {
        translate([head_size*0.5, i*head_size*0.3, head_size*0.3])
        sphere(head_size*0.15);
    }

    // Horn
    color(rock_color)
    translate([-head_size*0.3, 0, head_size*0.8])
    cylinder(h1=head_size*0.8, r1=head_size*0.2, r2=head_size*0.05);
}

module body_segment(index) {
    segment_width = segment_height * (1 - index * 0.08);

    color(rock_color)
    translate([0, 0, segment_height/2])
    scale([1.2 - index*0.05, 1, 1 - index*0.05])
    cube([segment_length, segment_width, segment_height], center=true);

    // Rock details
    color(accent_color)
    for(i = [0:3]) {
        rotate([0, 0, i*90])
        translate([segment_length*0.3, segment_width*0.3, segment_height*0.4])
        sphere(segment_height*0.2);
    }
}

module onix_body() {
    for(i = [0:body_segments-1]) {
        translate([i*segment_length*0.9, 0, -i*segment_height*0.3])
        rotate([0, 0, i*5])
        body_segment(i);
    }
}

module tail_spike() {
    color(rock_color)
    translate([0, 0, segment_height*0.5])
    scale([0.5, 0.5, 2])
    sphere(segment_height*0.8);
}

module onix_tail() {
    translate([(body_segments-1)*segment_length*0.9 + segment_length, 0, -(body_segments-1)*segment_height*0.3 - segment_height*0.5])
    tail_spike();
}

// Main assembly
translate([-20*scale_factor, 0, 0])
rotate([0, 0, 10])
union() {
    onix_head();
    onix_body();
    onix_tail();
}

