// Exeggcute - Realistic Style, Medium Complexity
// Scale to ~80mm width

module exeggcute() {
    // Colors
    color_light_purple = [0.85, 0.75, 0.9];
    color_dark_purple = [0.45, 0.3, 0.6];
    color_yolk = [1, 0.85, 0.2];
    color_eye = [0, 0, 0];
    color_stem = [0.1, 0.5, 0.2];
    color_crack = [0.3, 0.2, 0.4];
    color_shadow = [0.2, 0.1, 0.3];

    // Stem module
    module stem() {
        color(color_stem) {
            // Main stem
            translate([0, 0, -5]) {
                cylinder(h=10, r1=2.5, r2=1.5);
            }
            // Leaves
            rotate([0, -30, 0]) {
                translate([0, 3, -2]) {
                    scale([1, 0.3, 1]) {
                        sphere(3);
                    }
                }
            }
            rotate([0, 30, 180]) {
                translate([0, 3, -2]) {
                    scale([1, 0.3, 1]) {
                        sphere(3);
                    }
                }
            }
        }
    }

    // Single egg module
    module egg(rotation=[0,0,0], translation=[0,0,0], crack_type=0) {
        color(color_light_purple) {
            // Main egg body
            translate(translation) {
                rotate(rotation) {
                    scale([1, 1.2, 1]) {
                        sphere(12);
                    }

                    // Crack details
                    color(color_crack) {
                        if (crack_type == 0) {
                            // Large crack
                            translate([0, 0, 9]) {
                                linear_extrude(height=1) {
                                    polygon(points=[[0,0], [3,2], [1,4], [-1,3], [-2,5], [-4,2], [-2,0]]);
                                }
                            }
                        } else if (crack_type == 1) {
                            // Small crack
                            translate([0, 2, 8]) {
                                rotate([0, 90, 0]) {
                                    cylinder(h=8, r=0.5);
                                }
                            }
                        } else {
                            // Multiple cracks
                            for (i = [0:4]) {
                                rotate([0, 0, i*72]) {
                                    translate([0, 8, 5]) {
                                        cube([1, 4, 0.5], center=true);
                                    }
                                }
                            }
                        }
                    }

                    // Eyes
                    color(color_eye) {
                        // Left eye
                        translate([-4, 5, 8]) {
                            scale([1, 1.5, 0.5]) {
                                sphere(2);
                            }
                        }
                        // Right eye
                        translate([4, 5, 8]) {
                            scale([1, 1.5, 0.5]) {
                                sphere(2);
                            }
                        }
                    }

                    // Yolk showing through crack
                    color(color_yolk) {
                        translate([0, 0, 8.5]) {
                            sphere(4);
                        }
                    }

                    // Purple spots
                    color(color_dark_purple) {
                        for (phi = [30:60:150]) {
                            for (theta = [0:45:315]) {
                                rotate([phi, 0, theta]) {
                                    translate([0, 0, 11.5]) {
                                        sphere(1);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    // Create the cluster of 6 eggs
    union() {
        // Front center egg
        egg([0, 0, 0], [0, 0, 0], 0);

        // Top egg
        egg([0, -30, 0], [0, 15, 8], 1);

        // Bottom left egg
        egg([0, 30, -30], [-12, -8, -6], 2);

        // Bottom right egg
        egg([0, 30, 30], [12, -8, -6], 0);

        // Back left egg
        egg([30, 0, 45], [-8, -2, -12], 1);

        // Back right egg
        egg([-30, 0, -45], [8, -2, -12], 2);

        // Stems for bottom eggs
        translate([-12, -8, -16]) {
            rotate([0, 0, -30]) {
                stem();
            }
        }
        translate([12, -8, -16]) {
            rotate([0, 0, 30]) {
                stem();
            }
        }
    }
}

// Render the complete model
exeggcute();

