// Coconut heads with faces
module coconut_head(x, y, z, rot_x, rot_y, rot_z) {
    translate([x, y, z]) {
        rotate([rot_x, rot_y, rot_z]) {
            color(coconut_color) {
                // Main coconut
                sphere(d = 22);

                // Face area
