            for (i = [0:5:length]) {
                translate([i, 0, 0.5])
                    rotate([0, 0, i/2])
                        cube([length/10, width/2, 0.3], center = true);
                translate([i, 0, -0.5])
                    rotate([0, 0, -i/2])
                        cube([length/10, width/2, 0.3], center = true);
