// Medium complexity realistic design for 3D printing

// Main color definitions
body_color = [0.2, 0.8, 0.3];  // Bright green
vine_color = [0.15, 0.7, 0.25];  // Slightly darker green
eye_color = [0.9, 0.9, 0.9];  // White
pupil_color = [0.1, 0.1, 0.1];  // Black
mouth_color = [0.6, 0.2, 0.2];  // Dark red

// Main body module
module tangela_body() {
    color(body_color)
    translate([0, 0, 10])
    sphere(15, $fn=32);
}

// Vine generation module with realistic curves
module vine_segment(length, start_angle, end_angle, thickness) {
    color(vine_color)
    for(i = [0:2:length-1]) {
        translate([sin(start_angle + i*0.1) * i*0.8, 
                  cos(start_angle + i*0.1) * i*0.8, 
                  i*0.6])
        rotate([0, 0, start_angle + i*0.1])
        cylinder(h=3, r=thickness, $fn=8);
    }
}

// Create a realistic vine cluster
module vine_cluster(offset_x, offset_y, offset_z, rotation) {
    translate([offset_x, offset_y, offset_z])
    rotate([0, 0, rotation]) {
        // Main vine
        vine_segment(20, 0, 90, 2);

        // Secondary vines
        translate([5, 0, 5]) vine_segment(15, 30, 60, 1.5);
        translate([-5, 0, 3]) vine_segment(18, -30, 45, 1.8);
        translate([0, 5, 7]) vine_segment(12, 60, 30, 1.3);
        translate([0, -5, 4]) vine_segment(16, -60, 75, 1.6);
    }
}

// Eye module
module eye(x, y, z) {
    translate([x, y, z]) {
        color(eye_color)
        sphere(3, $fn=16);

        color(pupil_color)
        translate([0, 0, 2.1])
        sphere(1.5, $fn=16);
    }
}

// Mouth module
module mouth() {
    color(mouth_color)
    translate([0, -12, 8])
    rotate([90, 0, 0])
    cylinder(h=0.5, r1=3, r2=2, $fn=16);
}

// Vine detail module for realistic texture
module vine_detail() {
    // Create multiple vine clusters around the body
    vine_cluster(0, 0, 0, 0);
    vine_cluster(10, 5, 5, 45);
    vine_cluster(-8, -6, 3, -30);
    vine_cluster(6, -10, 7, 60);
    vine_cluster(-12, 8, 2, -45);
    vine_cluster(8, 12, 4, 120);
    vine_cluster(-15, 0, 6, 90);
    vine_cluster(0, -15, 1, -90);
}

// Base platform for stability
module base() {
    color([0.3, 0.3, 0.3])
    translate([0, 0, -2])
    cylinder(h=2, r=25, $fn=32);
}

// Main assembly
union() {
    // Base for stability
    base();

    // Main body
    tangela_body();

    // Vine details covering the body
    vine_detail();

    // Eyes peeking through vines
    eye(-5, -8, 15);
    eye(5, -8, 15);

    // Mouth
    mouth();

    // Additional small vines for realistic detail
    translate([0, 0, 20])
    vine_cluster(2, 2, 0, 180);
    translate([0, 0, 18])
    vine_cluster(-3, -3, 0, -135);
    translate([0, 0, 16])
    vine_cluster(1, -4, 0, 225);
}

