body_width = 25 * scale_factor;

module seaking_body() {
    color([0.9, 0.2, 0.1]) {
        // Main body - oval shape
        translate([0, 0, body_height/2])
            scale([1.2, 0.8, 1])
            sphere(d=body_width);

        // Extended tail section
        translate([-body_length/2, 0, body_height/2 - 5])
            scale([2, 0.6, 0.4])
            sphere(d=body_width);
    }
}

module seaking_head() {
    color([0.9, 0.2, 0.1]) {
        // Head sphere
        translate([body_length/2 + 5, 0, body_height/2 + 5])
            sphere(d=body_width * 0.8);

        // Snout extension
        translate([body_length/2 + 15, 0, body_height/2 + 5])
            scale([1.5, 0.6, 0.6])
            sphere(d=body_width * 0.5);
    }

    color([1, 0.9, 0.2]) {
        // Beak
        translate([body_length/2 + 20, 0, body_height/2 + 5])
            rotate([0, -20, 0])
            cylinder(h=8, d1=8, d2=5);
    }
}

module horn() {
    color([1, 0.9, 0.2]) {
        // Main horn
        translate([body_length/2 + 10, 0, body_height/2 + 15])
            rotate([0, 90, 0])
            cylinder(h=20, d1=3, d2=8);

        // Horn details
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([body_length/2 + 10, 0, body_height/2 + 15])
                rotate([0, 90, 0])
                translate([5, 0, 0])
                cylinder(h=8, d1=1, d2=3);
        }
    }
}

module tail_fin() {
    color([1, 0.9, 0.2]) {
        // Main tail fin
        translate([-body_length/2 - 10, 0, body_height/2 - 5])
            rotate([0, 0, 90])
            linear_extrude(height=2)
            polygon(points=[[0,0], [-15,8], [-25,0], [-15,-8]]);

        // Lower tail fin
        translate([-body_length/2 - 5, 0, body_height/2 - 15])
            rotate([0, 0, -30])
            scale([0.8, 0.6, 1])
            linear_extrude(height=2)
            polygon(points=[[0,0], [-10,5], [-15,0], [-10,-5]]);
    }
}

module dorsal_fin() {
    color([1, 0.9, 0.2]) {
        // Main dorsal fin
        translate([0, 0, body_height/2 + 15])
            rotate([0, 0, 0])
            linear_extrude(height=3)
            polygon(points=[[0,0], [-8,15], [0,12], [8,15]]);

        // Secondary dorsal fins
        for(i = [-1, 1]) {
            translate([i * 10, 0, body_height/2 + 10])
                rotate([0, 0, i * 30])
                scale([0.6, 0.6, 1])
                linear_extrude(height=2)
                polygon(points=[[0,0], [-5,8], [0,6], [5,8]]);
        }
    }
}

module side_fins() {
    color([1, 0.9, 0.2]) {
        // Left fin
        translate([body_length/4, body_width/2 + 5, body_height/2 - 5])
            rotate([0, 90, 0])
            scale([1, 1.5, 1])
            cylinder(h=12, d1=3, d2=8);

        // Right fin
        translate([body_length/4, -body_width/2 - 5, body_height/2 - 5])
            rotate([0, -90, 0])
            scale([1, 1.5, 1])
            cylinder(h=12, d1=3, d2=8);
    }
}

module eyes() {
    color([0, 0, 0]) {
        // Left eye
        translate([body_length/2 + 10, body_width/4, body_height/2 + 8])
            sphere(d=3);

        // Right eye
        translate([body_length/2 + 10, -body_width/4, body_height/2 + 8])
            sphere(d=3);
    }

    color([1, 1, 1]) {
        // Eye highlights
        translate([body_length/2 + 11, body_width/4 + 1, body_height/2 + 9])
            sphere(d=1);

        translate([body_length/2 + 11, -body_width/4 + 1, body_height/2 + 9])
            sphere(d=1);
    }
}

module belly_pattern() {
    color([1, 1, 0.8]) {
        // Belly oval
        difference() {
            translate([0, 0, body_height/2 - 8])
                scale([1.3, 0.7, 0.3])
                sphere(d=body_width * 0.9);

            // Remove back half
            translate([0, 0, body_height/2 - 15])
                cube([body_length * 2, body_width * 2, body_height], center=true);
        }
    }
}

// Assembly
union() {
    seaking_body();
    seaking_head();
    horn();
    tail_fin();
    dorsal_fin();
    side_fins();
    eyes();
    belly_pattern();
}

// Realistic Staryu - Medium Complexity OpenSCAD Model
// Size: ~80mm diameter

// Colors
staryu_orange = [1, 0.5, 0.2];
staryu_red = [0.8, 0.2, 0.1];
gold = [1, 0.8, 0];

// Main parameters
center_size = 20;
arm_length = 35;
arm_width = 12;
arm_thickness = 6;
joint_radius = 8;

// Central body
module central_body() {
    color(staryu_orange) {
        difference() {
            sphere(d = center_size, $fn = 32);

            // Carve out the center gem area
            translate([0, 0, center_size/2 - 2]) {
                cylinder(h = 5, d1 = 12, d2 = 10, $fn = 16);
            }
        }

        // Central gem
        color(gold) {
            translate([0, 0, center_size/2 - 1]) {
                cylinder(h = 3, d = 10, $fn = 16);
            }
        }
    }
}

// Single arm module
module arm() {
    color(staryu_orange) {
        // Main arm body
        rotate([0, 90, 0]) {
            cylinder(h = arm_length, d = arm_width, $fn = 16);
        }

        // Arm joint
        sphere(d = joint_radius, $fn = 16);

        // Arm tip - realistic claw
        translate([0, arm_length, 0]) {
            // Main claw
            rotate([0, 90, 0]) {
                cylinder(h = 8, d1 = arm_width, d2 = 8, $fn = 16);
            }

            // Claw tips
            for (a = [0, 120, 240]) {
                rotate([a, 0, 0]) {
                    translate([0, arm_length + 4, 0]) {
                        rotate([0, 90, 0]) {
                            cylinder(h = 6, d1 = 3, d2 = 1, $fn = 8);
                        }
                    }
                }
            }
        }

        // Arm segments
        for (i = [1:3]) {
            translate([0, i * arm_length/4, 0]) {
                rotate([90, 0, 0]) {
                    difference() {
                        cylinder(h = 1, d = arm_width + 2, $fn = 16);
                        cylinder(h = 1.2, d = arm_width, $fn = 16);
                    }
                }
            }
        }
    }
}

// Eye module
module eye() {
    color(st0, 0, 0) {
        // Main eye
        translate([0, 0, 0]) {
            sphere(d = 3, $fn = 16);
        }

        // Eye highlight
        color([1, 1, 1]) {
            translate([0.5, 0.5, 1.2]) {
                sphere(d = 1, $fn = 8);
            }
        }
    }
}

// Complete staryu
union() {
    // Central body
    central_body();

    // Five arms
    for (i = [0:4]) {
        rotate([0, 0, i * 72]) {
            translate([0, center_size/2, 0]) {
                arm();
            }
        }
    }

    // Eyes (5 eyes between arms)
    for (i = [0:4]) {
        rotate([0, 0, i * 72 + 36]) {
            translate([center_size/2 * 0.7, 0, center_size/2 * 0.7]) {
                eye();
            }
        }
    }

    // Back details
    color(staryu_red) {
        // Circular pattern on back
        translate([0, 0, -center_size/2 + 1]) {
            difference() {
                cylinder(h = 2, d = center_size - 4, $fn = 32);
                cylinder(h = 2.2, d = center_size - 8, $fn = 32);
            }
        }

        // Radial lines
        for (i = [0:4]) {
            rotate([0, 0, i * 72]) {
                translate([0, 0, -center_size/2 + 2]) {
                    cube([2, center_size/2 - 4, 2], center = true);
                }
            }
        }
    }
}

// Starmie - Realistic Style (Medium Complexity)
// Overall dimensions: ~80mm diameter, ~25mm thick

// Set colors
$fn = 64; // Smooth curves for realism

// Main body - purple gem
module main_body() {
    color([0.5, 0.2, 0.8]) 
    union() {
        // Central gem sphere
        sphere(d=30);

        // Gem facets
        for(i=[0:30:330]) {
            rotate([0, 0, i]) 
            translate([0, 12, 0])
            rotate([0, 30, 0])
            scale([1, 0.7, 0.3])
            sphere(d=8);
        }
    }
}

// Eye module for star points
module eye() {
    union() {
        // White sclera
        color([1, 1, 1])
        cylinder(h=3, d1=8, d2=8);

        // Red iris
        color([0.8, 0.2, 0.2])
        translate([0, 0, 1])
        cylinder(h=2, d1=6, d2=6);

        // Black pupil
        color([0, 0, 0])
        translate([0, 0, 2.5])
        cylinder(h=1, d1=3, d2=3);
    }
}

// Star point arm
module star_arm(length, offset_angle) {
    color([0.5, 0.2, 0.8])
    rotate([0, 0, offset_angle])
    union() {
        // Main arm
        translate([0, 0, -5])
        cylinder(h=length, d1=12, d2=8);

        // Eye at tip
        translate([0, 0, length-2])
        eye();

        // Ridges along arm
        for(i=[1:3:round(length/2)]) {
            translate([0, 0, i*2])
            cylinder(h=1, d1=14, d2=12);
        }
    }
}

// Center gem detail
module center_gem() {
    color([0.3, 0.1, 0.7])
    difference() {
        sphere(d=15);
        // Cut facets
        for(i=[0:45:315]) {
            rotate([0, 0, i])
            translate([7, 0, 0])
            cube([20, 0.5, 20], center=true);
        }
    }
}

// Assembly
union() {
    // Main body
    main_body();

    // Center gem detail
    translate([0, 0, 15])
    center_gem();

    // Five star points (Starmie has 5 arms)
    for(i=[0:72:360]) {
        translate([0, 0, -5])
        star_arm(25, i);
    }
}

