// Scyther OpenSCAD model - Realistic style, medium complexity
// Dimensions: ~70mm tall, 3D printable

// Global parameters
scyther_height = 70;
wall_thickness = 1.2;

// Colors
color_body = "#4CAF50";
color_blades = "#8BC34A";
color_eyes = "#FF5722";
color_accents = "#2E7D32";

// Main body module
module scyther_body() {
    color(color_body)
    union() {
        // Torso - tapered cylinder
        translate([0, 0, 20])
            scale([1, 0.7, 1])
            cylinder(h=25, r1=12, r2=8, $fn=32);

        // Abdomen
        translate([0, 0, 10])
            sphere(r=10);

        // Chest plate
        translate([0, -8, 25])
            rotate([90, 0, 0])
            cylinder(h=15, r1=8, r2=5, $fn=4);
    }
}

// Head module
module scyther_head() {
    color(color_body)
    union() {
        // Main head shape
        translate([0, 0, 45])
            scale([1.2, 0.8, 1.5])
            sphere(r=10);

        // Snout
        translate([0, -12, 45])
            rotate([30, 0, 0])
            scale([0.6, 0.4, 1.2])
            cylinder(h=8, r1=6, r2=4, $fn=16);

        // Eyes (faceted)
        translate([-5, -12, 48])
            color(color_eyes)
            rotate([0, 0, 45])
            cube([3, 1, 4], center=true);

        translate([5, -12, 48])
            color(color_eyes)
            rotate([0, 0, -45])
            cube([3, 1, 4], center=true);

        // Mandibles
        translate([-4, -18, 42])
            rotate([0, 0, 20])
            cube([2, 6, 2]);

        translate([4, -18, 42])
            rotate([0, 0, -20])
            cube([2, 6, 2]);
    }
}

// Blade module
module scythe_blade(side) {
    color(color_blades)
    translate([side * 15, 0, 35])
        rotate([0, 0, side * 15])
        union() {
            // Upper arm
            translate([0, 0, 0])
                rotate([0, 0, -90])
                scale([0.8, 0.8, 1])
                cylinder(h=15, r=3, $fn=8);

            // Blade base
            translate([0, -15, 0])
                rotate([90, 0, 0])
                scale([1, 0.3, 1])
                cylinder(h=8, r=5, $fn=6);

            // Curved blade
            translate([0, -23, 0])
                rotate([90, 0, 0])
                scale([0.5, 1, 0.1])
                cylinder(h=25, r=15, $fn=64);

            // Blade edge
            translate([0, -23, -1])
                rotate([90, 0, 0])
                scale([0.4, 1, 0.05])
                cylinder(h=25, r=15, $fn=64);
        }
}

// Wing module
module scyther_wing(side) {
    color(color_accents)
    translate([side * 18, 0, 32])
        rotate([0, 0, side * -30])
        union() {
            // Wing base
            translate([0, 0, 0])
                rotate([0, 0, 90])
                scale([0.5, 1, 0.1])
                cylinder(h=12, r=8, $fn=16);

            // Wing membrane (simplified)
            for(i = [0:2]) {
                translate([i * 3 - 3, 5 + i * 2, 0])
                    rotate([0, 0, side * (i * 10)])
                    cube([2, 6 - i * 1.5, 0.5]);
            }
        }
}

// Leg module
module scyther_leg(side, position) {
    color(color_body)
    translate([side * 10, 0, position])
        union() {
            // Upper leg
            rotate([0, side * 15, 0])
                cylinder(h=12, r=2.5, $fn=8);

            // Lower leg
            translate([0, 0, -12])
                rotate([0, side * -30, 0])
                cylinder(h=10, r=2, $fn=8);

            // Foot
            translate([0, 0, -22])
                rotate([0, side * 15, 0])
                scale([1.5, 0.8, 0.5])
                sphere(r=3);
        }
}

// Tail module
module scyther_tail() {
    color(color_body)
    translate([0, 10, 10])
        rotate([45, 0, 0])
        union() {
            // Tail segments
            for(i = [0:3]) {
                translate([0, 0, -i * 5])
                    scale([1 - i * 0.15, 0.8 - i * 0.1, 1])
                    cylinder(h=5, r=4 - i, $fn=8);
            }
        }
}

// Assembly
union() {
    scyther_body();
    scyther_head();

    // Blades
    scythe_blade(1);
    scythe_blade(-1);

    // Wings
    scyther_wing(1);
    scyther_wing(-1);

    // Legs
    scyther_leg(1, 15);  // Front right
    scyther_leg(-1, 15); // Front left
    scyther_leg(1, 5);   // Back right
    scyther_leg(-1, 5);  // Back left

    // Tail
    scyther_tail();
}

