// Main module
module electabuzz() {
    // Body
    color(color_yellow) {
        // Torso
        translate([0, 0, 25]) {
            scale([1, 0.8, 1.2]) sphere(d=40);
        }

        // Chest pattern
        color(color_black) {
            translate([0, -15, 25]) {
                scale([0.4, 0.1, 0.8]) sphere(d=40);
            }
        }
    }

    // Head
    color(color_yellow) {
        translate([0, 0, 55]) {
            scale([1.2, 1, 1.1]) sphere(d=35);
        }

        // Snout
        translate([0, 20, 50]) {
            scale([0.8, 0.6, 0.5]) sphere(d=20);
        }
    }

    // Horns
    color(color_yellow) {
        // Left horn
        translate([-15, 0, 70]) {
            rotate([0, -20, 45]) cylinder(h=15, d1=8, d2=3);
        }

        // Right horn
        translate([15, 0, 70]) {
            rotate([0, 20, -45]) cylinder(h=15, d1=8, d2=3);
        }
    }

    // Ears
    color(color_yellow) {
        // Left ear
        translate([-20, 0, 65]) {
            rotate([0, 0, -30]) scale([0.5, 1, 2]) sphere(d=15);
        }

        // Right ear
        translate([20, 0, 65]) {
            rotate([0, 0, 30]) scale([0.5, 1, 2]) sphere(d=15);
        }
    }

    // Eyes
    color(color_white) {
        translate([-8, 15, 55]) sphere(d=6);
        translate([8, 15, 55]) sphere(d=6);
    }

    color(color_black) {
        translate([-8, 17, 57]) sphere(d=3);
        translate([8, 17, 57]) sphere(d=3);
    }

    // Mouth
    color(color_black) {
        translate([0, 25, 50]) scale([0.8, 0.2, 0.3]) cube([10, 5, 3]);
    }

    // Arms
    color(color_yellow) {
        // Left arm
        translate([-25, 0, 35]) {
            rotate([0, 0, 20]) cylinder(h=30, d1=12, d2=10);
            translate([0, 0, 30]) rotate([0, 0, -20]) cylinder(h=25, d1=10, d2=8);
        }

        // Right arm
        translate([25, 0, 35]) {
            rotate([0, 0, -20]) cylinder(h=30, d1=12, d2=10);
            translate([0, 0, 30]) rotate([0, 0, 20]) cylinder(h=25, d1=10, d2=8);
        }
    }

    // Hands
    color(color_yellow) {
        // Left hand
        translate([-45, -5, 30]) {
            sphere(d=12);
            // Fingers
            for(i = [-30, -10, 10, 30]) {
                rotate([0, 0, i]) translate([0, 6, 0]) cylinder(h=8, d1=3, d2=2);
            }
        }

        // Right hand
        translate([45, -5, 30]) {
            sphere(d=12);
            // Fingers
            for(i = [-30, -10, 10, 30]) {
                rotate([0, 0, i]) translate([0, 6, 0]) cylinder(h=8, d1=3, d2=2);
            }
        }
    }

    // Legs
    color(color_yellow) {
        // Left leg
        translate([-10, 0, 10]) cylinder(h=20, d1=15, d2=12);
        translate([-10, 0, 0]) sphere(d=12);

        // Right leg
        translate([10, 0, 10]) cylinder(h=20, d1=15, d2=12);
        translate([10, 0, 0]) sphere(d=12);
    }

    // Feet
    color(color_yellow) {
        // Left foot
        translate([-10, 5, -8]) scale([1.2, 1.8, 0.8]) sphere(d=15);

        // Right foot
        translate([10, 5, -8]) scale([1.2, 1.8, 0.8]) sphere(d=15);
    }

    // Tail
    color(color_yellow) {
        translate([0, -30, 20]) {
            rotate([30, 0, 0]) cylinder(h=35, d1=15, d2=8);
            translate([0, 0, 35]) sphere(d=12);
        }
    }

    // Stripes
    color(color_black) {
        // Body stripes
        translate([0, 0, 30]) scale([1.1, 1.1, 0.1]) sphere(d=45);
        translate([0, 0, 15]) scale([1.1, 1.1, 0.1]) sphere(d=45);

        // Arm stripes
        translate([-30, 0, 45]) rotate([0, 0, 20]) cylinder(h=1, d=14);
        translate([30, 0, 45]) rotate([0, 0, -20]) cylinder(h=1, d=14);

        // Leg stripes
        translate([-10, 0, 5]) cylinder(h=1, d=16);
        translate([10, 0, 5]) cylinder(h=1, d=16);
    }

    // Plug-like hands pattern
    color(color_red) {
        // Left hand pattern
        translate([-45, -5, 30]) {
            rotate([0, 90, 0]) cylinder(h=8, d=8);
            difference() {
                sphere(d=12);
                translate([0, 0, -6]) cylinder(h=12, d=6);
            }
        }

        // Right hand pattern
        translate([45, -5, 30]) {
            rotate([0, 90, 0]) cylinder(h=8, d=8);
            difference() {
                sphere(d=12);
                translate([0, 0, -6]) cylinder(h=12, d=6);
            }
        }
    }
}

