// Pinsir Pokemon model - Realistic style, medium complexity
// Overall size ~80mm

$fn = 50;  // Smooth curves

// Main colors
color_dark = [0.2, 0.15, 0.1];
color_light = [0.4, 0.35, 0.25];
color_accent = [0.9, 0.1, 0.1];

module pinsir() {
    // Body
    color(color_dark)
    translate([0, 0, 15])
    scale([1.2, 0.8, 1.5])
    sphere(r = 15);

    // Thorax
    color(color_dark)
    translate([0, 0, 25])
    scale([1.4, 0.9, 1.2])
    sphere(r = 12);

    // Abdomen
    color(color_dark)
    translate([0, 0, 5])
    scale([0.8, 0.6, 1.2])
    sphere(r = 10);

    // Head
    color(color_dark)
    translate([0, 0, 35])
    scale([1.6, 1.2, 1.4])
    sphere(r = 12);

    // Pincers
    module pincer(side) {
        color(color_light)
        translate([side * 20, 0, 35])
        rotate([0, 0, side * 20])
        union() {
            // Base
            translate([0, 0, 0])
            rotate([0, side * 30, 0])
            scale([3, 1, 1])
            sphere(r = 5);

            // First segment
            translate([side * 12, 0, 0])
            rotate([0, side * 20, 0])
            scale([8, 3, 2])
            cylinder(r1 = 3, r2 = 5, h = 12);

            // Second segment
            translate([side * 25, 0, -3])
            rotate([0, side * 15, 0])
            scale([6, 2, 2])
            cylinder(r1 = 2, r2 = 4, h = 15);

            // Claw
            translate([side * 38, 0, -5])
            rotate([0, side * 10, 0])
            scale([4, 1.5, 1.5])
            cylinder(r1 = 2, r2 = 3, h = 10);
        }

        // Teeth on pincers
        for(i = [1:3]) {
            color(color_light)
            translate([side * (25 + i * 4), 0, -5 + i])
            rotate([0, side * 15, 0])
            scale([0.8, 0.8, 0.8])
            cube([3, 2, 1], center = true);
        }
    }

    pincer(1);
    pincer(-1);

    // Wings
    color(color_light)
    translate([0, 0, 28])
    rotate([0, 0, 0])
    difference() {
        scale([2, 0.5, 0.3])
        sphere(r = 15);
        // Wing membrane pattern
        for(i = [1:3]) {
            translate([0, i * 3, 0])
            scale([0.9 - i * 0.1, 0.8, 0.8])
            sphere(r = 15);
        }
    }

    // Legs
    module leg(x, y, angle) {
        color(color_dark)
        translate([x, y, 12])
        rotate([0, 0, angle])
        union() {
            // Upper leg
            translate([0, 0, 0])
            rotate([0, 90, 0])
            scale([0.8, 0.8, 1])
            cylinder(r = 3, h = 10);

            // Lower leg
            translate([10, 0, -5])
            rotate([0, 90, 0])
            scale([0.6, 0.6, 1])
            cylinder(r = 2.5, h = 8);

            // Foot
            translate([18, 0, -8])
            rotate([0, 0, 30])
            scale([1.5, 0.5, 0.5])
            sphere(r = 3);
        }
    }

    // Front legs
    leg(8, -10, 45);
    leg(-8, -10, -45);

    // Middle legs
    leg(12, 0, 90);
    leg(-12, 0, -90);

    // Back legs
    leg(8, 10, 135);
    leg(-8, 10, -135);

    // Eyes
    color(color_accent)
    translate([6, -8, 38])
    sphere(r = 2);
    translate([-6, -8, 38])
    sphere(r = 2);

    // Horn
    color(color_light)
    translate([0, 0, 55])
    rotate([0, 0, 0])
    scale([0.8, 0.8, 1.5])
    cylinder(r1 = 4, r2 = 0, h = 12);
}

