// Main body module
module magikarp_body() {
    color(gold) {
        // Main fish body - two intersecting ellipsoids
        intersection() {
            scale([1.2, 0.8, 0.6]) sphere(d=40, $fn=50);
            translate([0, 0, -5]) scale([1.1, 0.9, 0.7]) sphere(d=40, $fn=50);
        }

        // Tail base
        translate([-35, 0, 0]) rotate([0, 0, 15]) 
            scale([0.8, 0.3, 0.4]) sphere(d=25, $fn=30);
    }
}

// Tail fin module
module tail_fin() {
    color(red) {
        // Main tail fin
        translate([-45, 0, 0]) rotate([0, 90, 0]) {
            difference() {
                scale([1.5, 1, 0.1]) sphere(d=30, $fn=40);
                translate([0, 0, -2]) cube([40, 30, 5], center=true);
            }
        }

        // Tail fin details
        for(i = [-1, 1]) {
            translate([-45, i*8, 0]) rotate([0, 90, i*20]) 
                scale([0.8, 0.4, 0.1]) sphere(d=15, $fn=20);
        }
    }
}

// Dorsal fin module
module dorsal_fin() {
    color(red) {
        translate([5, 0, 15]) rotate([0, 0, 0]) {
            difference() {
                scale([0.1, 1, 1]) sphere(d=20, $fn=20);
                translate([0, 0, -10]) cube([5, 20, 10], center=true);
            }
        }
    }
}

// Pectoral fins module
module pectoral_fins() {
    color(red) {
        for(side = [-1, 1]) {
            translate([0, side*20, -5]) rotate([0, side*20, 0]) {
                scale([0.5, 1, 0.1]) sphere(d=15, $fn=20);
            }
        }
    }
}

// Whiskers module
module whiskers() {
    color(white) {
        for(side = [-1, 1]) {
            // Left/right whiskers
            translate([15, side*8, 0]) rotate([0, 0, side*30]) {
                cylinder(h=12, d1=1.5, d2=0.5, $fn=8);
            }
            translate([15, side*12, 0]) rotate([0, 0, side*45]) {
                cylinder(h=15, d1=1.5, d2=0.5, $fn=8);
            }
        }
    }
}

// Face module
module face() {
    // Eyes
    for(side = [-1, 1]) {
        color(white) {
            translate([20, side*8, 8]) sphere(d=6, $fn=20);
        }
        color(black) {
            translate([22, side*8, 8]) sphere(d=3, $fn=15);
        }
    }

    // Mouth
    color(black) {
        translate([25, 0, -2]) rotate([0, 90, 0]) 
            cylinder(h=0.5, d1=8, d2=5, $fn=20);
    }

