    color(red) {
        for(side = [-1, 1]) {
            translate([18, side*8, 12]) rotate([0, 0, side*-30]) {
                scale([0.1, 1, 0.5]) sphere(d=8, $fn=10);
            }
        }
    }
}

// Scale pattern module
