// Kabuto in realistic style - medium complexity
// Overall size ~70mm

// Colors
shell_color = [0.2, 0.4, 0.4];
eye_color = [1, 0.2, 0.2];
highlight_color = [1, 1, 1];
dome_color = [0.6, 0.8, 0.8];

// Shell segments
module shell_segment(radius, height, angle) {
    rotate([0, 0, angle])
    translate([0, radius, 0])
    rotate([0, 90, 0])
    cylinder(h=height, r1=radius/2, r2=0, $fn=8);
}

// Main shell dome
module shell_dome() {
    color(dome_color) {
        // Main dome
        difference() {
            sphere(d=50, $fn=64);
            translate([0, 0, 25])
            cube([60, 60, 30], center=true);
        }

        // Shell segments
        for(i=[0:30:330]) {
            color(shell_color) {
                shell_segment(22, 15, i);
            }
        }

        // Center ridge
        translate([0, -25, 0])
        rotate([0, 90, 0])
        cylinder(h=50, r1=3, r2=3, $fn=8);
    }
}

// Eye stalk
module eye_stalk(x_pos) {
    color(shell_color) {
        // Stalk
        translate([x_pos, 0, 5])
        rotate([0, 15, 0])
        cylinder(h=15, r1=4, r2=3, $fn=16);

        // Eye
        translate([x_pos, 0, 20])
        rotate([0, 15, 0])
        color(eye_color)
        sphere(d=6, $fn=16);

        // Eye highlight
        translate([x_pos+2, 1, 21])
        rotate([0, 15, 0])
        color(highlight_color)
        sphere(d=2, $fn=8);
    }
}

// Body segment
module body_segment() {
    color(shell_color) {
        // Main body
        translate([0, 0, -15])
        scale([1, 0.8, 0.6])
        sphere(d=40, $fn=32);

        // Underside
        translate([0, 0, -25])
        rotate([0, 180, 0])
        cylinder(h=10, r1=15, r2=0, $fn=16);
    }
}

// Leg
module leg(x, y, angle, length) {
    color(shell_color) {
        // Upper segment
        translate([x, y, -20])
        rotate([0, 0, angle])
        rotate([45, 0, 0])
        cylinder(h=length/2, r1=3, r2=2.5, $fn=8);

        // Lower segment
        translate([x+cos(angle)*length/2, y+sin(angle)*length/2, -20-length/2])
        rotate([0, 0, angle])
        rotate([45, 0, 0])
        cylinder(h=length/2, r1=2.5, r2=2, $fn=8);

        // Foot
        translate([x+cos(angle)*length, y+sin(angle)*length, -20-length])
        rotate([0, 0, angle])
        rotate([45, 0, 0])
        sphere(d=4, $fn=8);
    }
}

// Tail segment
module tail_segment() {
    color(shell_color) {
        // Main tail
        translate([0, -25, -15])
        rotate([0, 15, 0])
        cylinder(h=20, r1=5, r2=3, $fn=8);

        // Tail tip
        translate([0, -40, -12])
        sphere(d=4, $fn=8);
    }
}

// Assembly
union() {
    // Main shell
    shell_dome();

    // Body under shell
    body_segment();

    // Eye stalks
    eye_stalk(10);
    eye_stalk(-10);

    // Legs - 6 total
    leg(15, 10, 30, 15);
    leg(15, -10, -30, 15);
    leg(0, 15, 0, 18);
    leg(0, -15, 0, 18);
    leg(-15, 10, -30, 15);
    leg(-15, -10, 30, 15);

    // Tail
    tail_segment();
}

