// Dratini OpenSCAD Model - Realistic Style, Medium Complexity
// 3D printable design with proper wall thickness and no overhangs

// Global parameters
$fn = 36;
scale_factor = 0.4;
body_length = 60 * scale_factor;
body_diameter = 12 * scale_factor;
head_diameter = 15 * scale_factor;
ear_length = 8 * scale_factor;
tail_length = 40 * scale_factor;

// Main module
module dratini() {
    // Body color - light blue
    color([0.56, 0.77, 0.91]) {
        // Head
        translate([0, 0, body_length/2 + head_diameter/2]) {
            sphere(d = head_diameter);

            // Snout
            translate([0, head_diameter/2, 0]) {
                scale([1, 1.2, 0.8]) {
                    sphere(d = head_diameter * 0.8);
                }
            }
        }

        // Main body segments
        for(i = [0:5]) {
            translate([0, 0, body_length/2 - i * body_diameter * 0.8]) {
                sphere(d = body_diameter - i * 0.5);
            }
        }

        // Tail - tapered cylinder with spheres
        for(i = [0:8]) {
            translate([0, 0, -body_length/2 - i * body_diameter * 0.6]) {
                sphere(d = body_diameter * (1 - i * 0.08));
            }
        }
    }

    // White belly
    color([0.95, 0.95, 0.9]) {
        difference() {
            // Main body
            translate([0, 0, 0]) {
                scale([1, 0.8, 1]) {
                    sphere(d = body_length);
                }
            }

            // Cut bottom half
            translate([0, 0, -body_length]) {
                cube([body_length*2, body_length*2, body_length*2], center = true);
            }
        }
    }

    // Ears
    color([0.56, 0.77, 0.91]) {
        // Left ear
        translate([-head_diameter/2, head_diameter/2, body_length/2 + head_diameter/2 + ear_length/2]) {
            rotate([0, 30, -30]) {
                cylinder(h = ear_length, d1 = ear_length/2, d2 = ear_length/4);
            }
        }

        // Right ear
        translate([head_diameter/2, head_diameter/2, body_length/2 + head_diameter/2 + ear_length/2]) {
            rotate([0, 30, 30]) {
                cylinder(h = ear_length, d1 = ear_length/2, d2 = ear_length/4);
            }
        }
    }

    // Eyes
    color([0.1, 0.1, 0.1]) {
        // Left eye
        translate([-head_diameter/3, head_diameter/2, body_length/2 + head_diameter/2]) {
            sphere(d = head_diameter/8);
        }

        // Right eye
        translate([head_diameter/3, head_diameter/2, body_length/2 + head_diameter/2]) {
            sphere(d = head_diameter/8);
        }
    }

    // Nose
    color([0.2, 0.2, 0.2]) {
        translate([0, head_diameter * 0.7, body_length/2 + head_diameter/2]) {
            sphere(d = head_diameter/10);
        }
    }

    // Fin on back
    color([0.4, 0.6, 0.8]) {
        for(i = [0:4]) {
            translate([0, 0, body_length/2 - i * body_diameter * 1.2]) {
                rotate([0, 0, 0]) {
                    linear_extrude(height = 2) {
                        polygon(points = [
                            [0, -body_diameter/2],
                            [-body_diameter/3, -body_diameter/2 - 3],
                            [body_diameter/3, -body_diameter/2 - 3]
                        ]);
                    }
                }
            }
        }
    }

    // Tail fin
    color([0.4, 0.6, 0.8]) {
        translate([0, 0, -body_length/2 - tail_length]) {
            rotate([0, 0, 0]) {
                linear_extrude(height = 1.5) {
                    polygon(points = [
                        [0, 0],
                        [-body_diameter/2, -body_diameter * 0.8],
                        [body_diameter/2, -body_diameter * 0.8]
                    ]);
                }
            }
        }
    }
}

