// Ledyba - Realistic Style, Medium Complexity
// 3D printable Pokemon model

// Main parameters
scale_factor = 0.8;
body_radius = 12 * scale_factor;
head_radius = 8 * scale_factor;
eye_radius = 3 * scale_factor;
spot_radius = 1.2 * scale_factor;
leg_length = 15 * scale_factor;
leg_width = 2 * scale_factor;
antenna_length = 20 * scale_factor;
antenna_width = 1 * scale_factor;
wing_length = 25 * scale_factor;
wing_width = 18 * scale_factor;

// Colors
color_red = [0.8, 0.2, 0.2];
color_orange = [1, 0.6, 0.2];
color_black = [0.1, 0.1, 0.1];
color_white = [0.95, 0.95, 0.95];

module ledyba_body() {
    // Main body shell
    color(color_red)
    union() {
        // Central body
        difference() {
            sphere(r = body_radius, $fn = 32);
            // Flatten bottom
            translate([0, 0, -body_radius])
                cube([body_radius*2.2, body_radius*2.2, body_radius*2], center = true);
        }

        // Head
        translate([0, body_radius*0.7, body_radius*0.3])
            sphere(r = head_radius, $fn = 32);
    }
}

module ledyba_eyes() {
    color(color_black)
    union() {
        // Left eye
        translate([-head_radius*0.5, body_radius*0.7 + head_radius*0.6, body_radius*0.3 + head_radius*0.2])
            sphere(r = eye_radius, $fn = 16);

        // Right eye
        translate([head_radius*0.5, body_radius*0.7 + head_radius*0.6, body_radius*0.3 + head_radius*0.2])
            sphere(r = eye_radius, $fn = 16);
    }

    // Eye highlights
    color(color_white)
    union() {
        translate([-head_radius*0.4, body_radius*0.7 + head_radius*0.65, body_radius*0.3 + head_radius*0.35])
            sphere(r = eye_radius*0.4, $fn = 8);

        translate([head_radius*0.6, body_radius*0.7 + head_radius*0.65, body_radius*0.3 + head_radius*0.35])
            sphere(r = eye_radius*0.4, $fn = 8);
    }
}

module ledyba_spots() {
    color(color_black)
    for(i = [-1, 1]) {
        for(j = [-1, 1]) {
            translate([i * body_radius * 0.6, j * body_radius * 0.6, body_radius * 0.7])
                sphere(r = spot_radius, $fn = 8);
        }
    }

    // Additional spots
    translate([0, body_radius * 0.8, body_radius * 0.8])
        sphere(r = spot_radius * 0.8, $fn = 8);
    translate([0, -body_radius * 0.8, body_radius * 0.8])
        sphere(r = spot_radius * 0.8, $fn = 8);
}

module ledyba_antenna() {
    color(color_black)
    union() {
        // Left antenna
        translate([-head_radius*0.8, body_radius*0.7 + head_radius*0.5, body_radius*0.3 + head_radius*0.5])
            rotate([0, 0, -30])
                cylinder(h = antenna_length, r = antenna_width, $fn = 8);

        // Right antenna
        translate([head_radius*0.8, body_radius*0.7 + head_radius*0.5, body_radius*0.3 + head_radius*0.5])
            rotate([0, 0, 30])
                cylinder(h = antenna_length, r = antenna_width, $fn = 8);
    }

    // Antenna balls
    color(color_orange)
    union() {
        translate([-head_radius*0.8 - antenna_length*sin(30), 
                  body_radius*0.7 + head_radius*0.5 + antenna_length*cos(30), 
                  body_radius*0.3 + head_radius*0.5])
            sphere(r = antenna_width*2, $fn = 8);

        translate([head_radius*0.8 + antenna_length*sin(30), 
                  body_radius*0.7 + head_radius*0.5 + antenna_length*cos(30), 
                  body_radius*0.3 + head_radius*0.5])
            sphere(r = antenna_width*2, $fn = 8);
    }
}

module ledyba_legs() {
    color(color_black)
    for(i = [0:3]) {
        angle = i * 90;
        rotate([0, 0, angle])
            translate([body_radius * 0.8, 0, -body_radius * 0.3])
                rotate([30, 0, 0])
                    cylinder(h = leg_length, r = leg_width, $fn = 8);
    }

    // Feet
    color(color_black)
    for(i = [0:3]) {
        angle = i * 90;
        rotate([0, 0, angle])
            translate([body_radius * 0.8 + leg_length*sin(30), 0, -body_radius*0.3 - leg_length*cos(30)])
                sphere(r = leg_width*1.5, $fn = 8);
    }
}

module ledyba_wings() {
    color(color_orange)
    difference() {
        // Left wing
        translate([-body_radius*1.2, 0, body_radius*0.5])
            rotate([0, 0, -20])
                scale([1, 0.7, 0.1])
                    sphere(r = wing_length, $fn = 32);

        // Wing cutouts
        translate([-body_radius*1.4, -wing_width*0.3, body_radius*0.6])
            cube([wing_length*0.4, wing_width*0.6, 1]);
        translate([-body_radius*1.6, 0, body_radius*0.6])
            cube([wing_length*0.3, wing_width*0.4, 1]);
    }

    difference() {
        // Right wing
        translate([body_radius*1.2, 0, body_radius*0.5])
            rotate([0, 0, 20])
                scale([1, 0.7, 0.1])
                    sphere(r = wing_length, $fn = 32);

        // Wing cutouts
        translate([body_radius*1.4, -wing_width*0.3, body_radius*0.6])
            cube([wing_length*0.4, wing_width*0.6, 1]);
        translate([body_radius*1.6, 0, body_radius*0.6])
            cube([wing_length*0.3, wing_width*0.4, 1]);
    }
}

// Assembly
ledyba_body();
ledyba_eyes();
ledyba_spots();
ledyba_antenna();
ledyba_legs();
ledyba_wings();

