// Spinarak Pokemon - Realistic Style, Medium Complexity
// Overall size: 60mm wide x 80mm long x 40mm tall

// Main colors
$face_color = "#8B4513";      // Brown
$body_color = "#2F4F4F";      // Dark Slate Gray
$marking_color = "#FF69B4";   // Hot Pink
$eye_color = "#000000";       // Black
$highlight_color = "#FFFFFF"; // White

module spinarak() {
    // Main body
    union() {
        // Abdomen (spider body)
        color($body_color)
            translate([0, 0, 15])
                scale([1.2, 1.5, 1])
                    sphere(d=30, $fn=32);

        // Cephalothorax (head/body front)
        color($body_color)
            translate([0, -20, 20])
                scale([0.8, 0.9, 0.7])
                    sphere(d=25, $fn=32);

        // Face mask
        color($face_color)
            translate([0, -28, 18])
                scale([0.9, 0.6, 0.3])
                    sphere(d=20, $fn=32);
    }

    // Eyes (4 total - 2 large, 2 small)
    // Large eyes
    for(i = [-1, 1]) {
        color($eye_color)
            translate([i*8, -25, 22])
                sphere(d=6, $fn=16);
        color($highlight_color)
            translate([i*8.5, -24, 23])
                sphere(d=2, $fn=8);
    }

    // Small eyes
    for(i = [-1, 1]) {
        color($eye_color)
            translate([i*4, -20, 20])
                sphere(d=3, $fn=8);
    }

    // Mandibles (fangs)
    for(i = [-1, 1]) {
        color($face_color)
            translate([i*6, -32, 15])
                rotate([0, 0, i*20])
                    cylinder(h=8, d1=4, d2=2, $fn=8);
    }

    // Legs (6 legs total - 3 pairs)
    module leg(angle, length, height) {
        translate([0, 0, height])
            rotate([0, 0, angle])
                translate([15, -5, 0])
                    rotate([90, 0, 0])
                        cylinder(h=length, d=3, $fn=8);
    }

    // Front legs
    for(i = [-1, 1]) {
        leg(i*40, 25, 20);
    }

    // Middle legs
    for(i = [-1, 1]) {
        leg(i*70, 30, 15);
    }

    // Rear legs
    for(i = [-1, 1]) {
        leg(i*110, 28, 10);
    }

    // Pink markings on abdomen
    color($marking_color)
        translate([0, 0, 25])
            scale([1.3, 1.6, 0.1])
                sphere(d=30, $fn=32);

    // Diamond pattern on back
    color($marking_color)
        for(x = [-15, 0, 15]) {
            for(y = [-20, -5, 10]) {
                translate([x, y, 29])
                    scale([0.2, 0.2, 0.05])
                        cube([10, 10, 1], center=true);
            }
        }

    // Stinger on rear
    color($body_color)
        translate([0, 35, 15])
            rotate([0, 90, 0])
                cylinder(h=8, d1=5, d2=3, $fn=8);
}

