wall = 1.2;                     // minimum wall thickness for FDM

module pichu() {
    // main body assembly
    union() {
        // head
        translate([0, 0, 45])
        color("yellow")
        sphere(d = 35);

        // torso
        translate([0, 0, 20])
        color("yellow")
        sphere(d = 30);

        // ears
        for (x = [-1, 1])
            translate([x*12, 0, 60])
            rotate([0, x*10, 0])
            ear(x);

        // arms
        for (x = [-1, 1])
            translate([x*17, 0, 30])
            rotate([0, 0, x*20])
            arm(x);

        // legs
        for (x = [-1, 1])
            translate([x*9, 0, 8])
            leg(x);

        // tail
        translate([0, -22, 25])
        rotate([90, 0, 0])
        tail();

        // facial details
        face();
    }
}

// ear module with black tip
module ear(side) {
    color("yellow")
    cylinder(h = 20, d1 = 8, d2 = 6);
    translate([0, 0, 20])
    color("black")
    cylinder(h = 8, d1 = 6, d2 = 0);
}

// arm module
module arm(side) {
    color("yellow")
    rotate([0, 90, 0])
    cylinder(h = 12, d = 6);
}

// leg module
module leg(side) {
    color("yellow")
    translate([0, 0, -8])
    cylinder(h = 16, d1 = 9, d2 = 6);
}

// tail module  lightning bolt shape built from stacked blocks
module tail() {
    color("black")
    union() {
        // vertical stem
        cube([8, 8, 25], center = true);

        // first zig
        translate([6, 0, 8])
        cube([10, 8, 8], center = true);

        // second zag
        translate([-5, 0, -8])
        cube([10, 8, 8], center = true);

        // base
        translate([0, 0, -18])
        cube([10, 8, 8], center = true);
    }
}

// facial features
