    // eyes
    for (x = [-1, 1])
        translate([x*7, 12, 45])
        color("black")
        sphere(d = 4);

    // red cheek pouches
    for (x = [-1, 1])
        translate([x*11, 8, 42])
        color("red")
        sphere(d = 5);

    // mouth
    translate([0, 15, 40])
    rotate([90, 0, 0])
    color("black")
    cylinder(h = 1, d1 = 6, d2 = 0);
}

