// Natu - Realistic Style (medium complexity)
// Height: ~60mm when printed

// Parameters
body_radius = 12;
head_radius = 15;
beak_length = 8;
beak_width = 6;
beak_height = 4;
eye_radius = 3;
pupil_radius = 1.5;
wing_length = 20;
wing_width = 15;
tail_length = 18;
tail_width = 12;
leg_length = 8;
leg_width = 3;
foot_length = 5;
foot_width = 4;

// Colors
color_body = [0.8, 0.2, 0.2];      // Red
color_wings = [0.9, 0.9, 0.9];    // Light gray
color_beak = [1, 0.8, 0];         // Yellow
color_eyes = [0.1, 0.1, 0.1];     // Black
color_legs = [1, 0.8, 0];         // Yellow

module natu_body() {
    // Main body sphere
    color(color_body)
    sphere(r=body_radius, $fn=32);

    // Head
    translate([0, 0, body_radius + head_radius - 5])
    color(color_body)
    sphere(r=head_radius, $fn=32);
}

module natu_beak() {
    // Main beak
    translate([head_radius - 2, 0, body_radius + head_radius])
    rotate([0, 90, 0])
    color(color_beak)
    cylinder(h=beak_length, r1=beak_width/2, r2=0, $fn=16);
}

module natu_eyes() {
    // Left eye
    translate([head_radius - 3, -6, body_radius + head_radius + 5])
    color(color_eyes)
    sphere(r=eye_radius, $fn=16);

    // Left pupil
    translate([head_radius - 1.5, -6.5, body_radius + head_radius + 6])
    color([0, 0, 0])
    sphere(r=pupil_radius, $fn=8);

    // Right eye
    translate([head_radius - 3, 6, body_radius + head_radius + 5])
    color(color_eyes)
    sphere(r=eye_radius, $fn=16);

    // Right pupil
    translate([head_radius - 1.5, 6.5, body_radius + head_radius + 6])
    color([0, 0, 0])
    sphere(r=pupil_radius, $fn=8);
}

module natu_wing() {
    // Wing base
    color(color_wings)
    difference() {
        // Main wing shape
        translate([0, 0, -wing_width/2])
        cube([wing_length, wing_width/3, wing_width], center=true);

        // Wing tip cutout
        translate([wing_length/2, 0, -wing_width/2 - 1])
        cube([wing_length/2 + 2, wing_width + 2, wing_width + 2], center=true);
    }

    // Wing feathers
    for(i = [1:3]) {
        translate([-5 + i*3, 0, wing_width/4])
        rotate([0, -30 + i*15, 0])
        color(color_wings)
        cube([wing_length/2, wing_width/6, wing_width/2]);
    }
}

module natu_wings() {
    // Left wing
    translate([-body_radius - 2, -body_radius - 5, body_radius])
    rotate([0, 0, 30])
    natu_wing();

    // Right wing
    translate([-body_radius - 2, body_radius + 5, body_radius])
    rotate([0, 0, -30])
    mirror([0, 1, 0])
    natu_wing();
}

module natu_tail() {
    // Tail base
    translate([-body_radius - tail_length/2, 0, -body_radius/2])
    color(color_wings)
    cube([tail_length, tail_width/3, tail_width], center=true);

    // Tail feathers
    for(i = [-1:1]) {
        translate([-body_radius - tail_length + 2, i*3, -body_radius/2 + i*2])
        rotate([0, 0, -20 + i*20])
        color(color_wings)
        cube([tail_length/2, 2, 4]);
    }
}

module natu_leg() {
    // Upper leg
    color(color_legs)
    cylinder(h=leg_length, r=leg_width/2, $fn=8);

    // Foot
    translate([0, foot_width/2, -leg_length])
    color(color_legs)
    cube([foot_length, foot_width, leg_width/2], center=true);

    // Toes
    for(i = [-1, 0, 1]) {
        translate([foot_length/2 + 1, i*foot_width/3, -leg_length])
        color(color_legs)
        cube([2, 1, 1]);
    }
}

module natu_legs() {
    // Left leg
    translate([0, -body_radius/2, -body_radius - leg_length/2])
    rotate([0, 10, 0])
    natu_leg();

    // Right leg
    translate([0, body_radius/2, -body_radius - leg_length/2])
    rotate([0, -10, 0])
    natu_leg();
}

// Main assembly
union() {
    natu_body();
    natu_beak();
    natu_eyes();
    natu_wings();
    natu_tail();
    natu_legs();
}

