// Jumpluff OpenSCAD Model  Realistic / Medium Complexity
// ~80 mm tall when printed

// Color palette
c_body       = "#c2e6ff";
c_puff       = "#ffffff";
c_eyes       = "#0066cc";
c_mouth      = "#ff6666";
c_stem       = "#4d9900";
c_detail     = "#b3e6ff";

// Overall scale factor
sf = 1.0;

module bodySphere() {
    color(c_body)
    sphere(d = 22 * sf, $fn = 60);
}

module bluePuff(d) {
    translate([0, 0, -d / 2])
    color(c_puff)
    sphere(d = d * sf, $fn = 40);
}

module eye(x, y, z) {
    translate([x * sf, y * sf, z * sf]) {
        color(c_eyes)
        sphere(d = 4 * sf, $fn = 20);
        translate([0, 0, 2.1 * sf])
        color("white")
        sphere(d = 1.5 * sf, $fn = 12);
    }
}

module mouth() {
    translate([0, 10.5 * sf, -6 * sf])
    rotate([0, 0, 180])
    color(c_mouth)
    cylinder(h = 4 * sf, r1 = 3 * sf, r2 = 1 * sf, $fn = 24);
}

module ear(angle) {
    rotate([0, 0, angle])
    translate([0, 11 * sf, 5 * sf])
    rotate([90, 0, 0])
    color(c_body)
    cylinder(h = 18 * sf, r1 = 4 * sf, r2 = 0, $fn = 32);
}

module cottonStem(h) {
    translate([0, 0, h / 2])
    color(c_stem)
    cylinder(h = h * sf, r = 1.5 * sf, $fn = 16);
}

module cottonPuff(d) {
    rotate([0, 0, 45])
    cottonStem(d * 0.8);
    translate([0, 0, d * 0.8])
    bluePuff(d);
}

module cottonTriple() {
    for (a = [0, 120, 240])
        rotate([0, 0, a])
        translate([18 * sf, 0, 0])
        cottonPuff(15);
}

module foot(x, y, z, side) {
    translate([x * sf, y * sf, z * sf])
    rotate([0, 0, side ? 20 : -20])
    color(c_body)
    union() {
        translate([0, 0, 4 * sf])
        sphere(d = 6 * sf, $fn = 32);
        translate([0, 0, 1 * sf])
        cylinder(h = 8 * sf, r1 = 3 * sf, r2 = 2 * sf, $fn = 24);
    }
}

module jumpluff() {
    // central body
    bodySphere();

    // ears
    ear(30);
    ear(-30);

    // eyes
    eye(-4, 8, 3);
    eye(4, 8, 3);

    // mouth
    mouth();

    // small blue puffs on head
    translate([0, 8, 12]) bluePuff(10);

    // cotton trio
    translate([0, 0, 15])
    cottonTriple();

    // feet
    foot(-7, 7, -10, false);
    foot(7, 7, -10, true);

    // tail stem
    translate([0, -11, -11])
    rotate([90, 0, 0])
    cottonPuff(20);
}

// render
jumpluff();

// Aipom Pokemon - Realistic Style, Medium Complexity
// 3D printable model using basic OpenSCAD primitives

// Main parameters
scale_factor = 1.2;
body_height = 35 * scale_factor;
head_radius = 12 * scale_factor;
ear_length = 8 * scale_factor;
arm_length = 20 * scale_factor;
tail_length = 45 * scale_factor;

// Colors
color_body = "#8B4513";
color_face = "#F5DEB3";
color_purple = "#8A2BE2";
color_black = "#000000";

// Head module
module head() {
    color(color_body) union() {
        // Main head sphere
        translate([0, 0, body_height + head_radius]) 
            sphere(r = head_radius, $fn = 32);

        // Face lighter area
        color(color_face) 
            translate([0, head_radius - 2, body_height + head_radius - 3])
            scale([1.2, 0.8, 0.8])
            sphere(r = head_radius * 0.8, $fn = 32);

        // Ears
        for(side = [-1, 1]) {
            color(color_body)
            translate([side * head_radius * 0.9, 0, body_height + head_radius * 1.5])
            rotate([0, side * 20, 0])
            cylinder(h = ear_length, r1 = ear_length/2, r2 = ear_length/3, $fn = 16);
        }
    }
}

// Eyes module
module eyes() {
    for(side = [-1, 1]) {
        // Eye whites
        color("white")
        translate([side * head_radius * 0.5, head_radius - 1, body_height + head_radius + 2])
        sphere(r = head_radius * 0.3, $fn = 16);

        // Pupils
        color(color_black)
        translate([side * head_radius * 0.6, head_radius - 0.5, body_height + head_radius + 3])
        sphere(r = head_radius * 0.15, $fn = 16);
    }
}

// Nose and mouth
module face_features() {
    // Nose
    color(color_black)
    translate([0, head_radius - 0.5, body_height + head_radius])
    sphere(r = head_radius * 0.2, $fn = 16);

    // Mouth
    color(color_black)
    translate([0, head_radius - 0.3, body_height + head_radius - 5])
    rotate([90, 0, 0])
    cylinder(h = 0.5, r1 = 3, r2 = 2, $fn = 16);
}

// Body module
module body() {
    color(color_body) union() {
        // Main body
        translate([0, 0, body_height/2])
        scale([1.5, 1, 1.2])
        sphere(r = body_height/2.5, $fn = 32);

        // Belly lighter patch
        color(color_face)
        translate([0, body_height/2.5 - 1, body_height/2])
        scale([1, 0.8, 0.6])
        sphere(r = body_height/3, $fn = 32);
    }
}

// Arms module
module arms() {
    for(side = [-1, 1]) {
        color(color_body) union() {
            // Upper arm
            translate([side * body_height/2, 0, body_height * 0.7])
            rotate([0, side * 30, 0])
            cylinder(h = arm_length/2, r = arm_length/6, $fn = 16);

            // Lower arm
            translate([side * (body_height/2 + arm_length/2 * sin(30)), 
                      arm_length/2 * cos(30), 
                      body_height * 0.7 - arm_length/2 * sin(30)])
            rotate([0, side * 45, 0])
            cylinder(h = arm_length/2, r = arm_length/6, $fn = 16);

            // Hand
            translate([side * (body_height/2 + arm_length * sin(30)), 
                      arm_length * cos(30), 
                      body_height * 0.7 - arm_length * sin(30)])
            sphere(r = arm_length/5, $fn = 16);
        }
    }
}

// Legs module
module legs() {
    for(side = [-1, 1]) {
        color(color_body) union() {
            // Thigh
            translate([side * body_height/3, 0, body_height/4])
            rotate([0, side * 10, 0])
            cylinder(h = arm_length/2, r = arm_length/5, $fn = 16);

            // Lower leg
            translate([side * (body_height/3 + arm_length/4 * sin(10)), 
                      0, 
                      body_height/4 - arm_length/4 * cos(10)])
            rotate([0, side * 10, 0])
            cylinder(h = arm_length/2, r = arm_length/6, $fn = 16);

            // Feet
            translate([side * (body_height/3 + arm_length/2 * sin(10)), 
                      arm_length/6, 
                      -2])
            scale([1.5, 2, 0.8])
            sphere(r = arm_length/5, $fn = 16);
        }
    }
}

// Tail with hand
module tail() {
    color(color_purple) union() {
        // Tail segments
        for(i = [0:4]) {
            translate([0, -body_height/2 - i * tail_length/5, 
                      body_height/2 - i * tail_length/10])
            rotate([0, 0, -i * 15])
            scale([1 - i * 0.15, 1 - i * 0.15, 1 - i * 0.1])
            cylinder(h = tail_length/5, r1 = tail_length/12, r2 = tail_length/15, $fn = 16);
        }

        // Hand at end of tail
        translate([0, -body_height/2 - tail_length, body_height/2 - tail_length/2])
        rotate([0, 0, -75])
        scale([1.5, 1, 0.8])
        sphere(r = tail_length/8, $fn = 16);

        // Fingers on tail hand
        for(finger = [-1, 0, 1]) {
            translate([finger * tail_length/12, 
                      -body_height/2 - tail_length - tail_length/16, 
                      body_height/2 - tail_length/2])
            rotate([0, 0, -75])
            rotate([finger * 20, 0, 0])
            cylinder(h = tail_length/12, r = tail_length/24, $fn = 8);
        }
    }
}

// Main assembly
union() {
    body();
    head();
    eyes();
    face_features();
    arms();
    legs();
    tail();
}

// Sunkern (Realistic Style, Medium Complexity)
// Size: ~60mm tall
// Colors: Brown body, green leaves, yellow face

$fn = 32;

// Main colors
brown = [0.4, 0.25, 0.15];
green = [0.2, 0.6, 0.2];
yellow = [1, 0.9, 0.2];
black = [0.1, 0.1, 0.1];
white = [1, 1, 1];

module sunkern() {
    // Body - main seed shape
    union() {
        // Main body
        translate([0, 0, 15]) {
            color(brown) {
                sphere(d = 40);
            }
        }

        // Bottom tapered part
        translate([0, 0, 5]) {
            color(brown) {
                cylinder(h = 15, d1 = 30, d2 = 35);
            }
        }

        // Face area
        translate([0, 18, 22]) {
            color(yellow) {
                sphere(d = 28);
            }
        }

        // Eyes
        for(x = [-8, 8]) {
            translate([x, 25, 25]) {
                color(black) {
                    sphere(d = 6);
                    translate([0, 0, 3]) {
                        color(white) {
                            sphere(d = 2);
                        }
                    }
                }
            }
        }

        // Mouth
        translate([0, 24, 18]) {
            color(black) {
                rotate([90, 0, 0]) {
                    cylinder(h = 2, d = 8);
                }
            }
        }

        // Leaves module
        module leaf() {
            color(green) {
                translate([0, 0, 5]) {
                    // Main leaf blade
                    rotate([0, 30, 0]) {
                        scale([1, 0.3, 3]) {
                            sphere(d = 15);
                        }
                    }

                    // Leaf tip
                    translate([0, 0, 10]) {
                        scale([0.8, 0.2, 1]) {
                            sphere(d = 10);
                        }
                    }
                }
            }
        }

        // Top leaves - 4 arranged in cross pattern
        for(angle = [0, 90, 180, 270]) {
            rotate([0, 0, angle]) {
                translate([0, 0, 40]) {
                    leaf();
                }
            }
        }

        // Side leaves
        translate([15, 0, 30]) {
            rotate([0, 0, -45]) {
                leaf();
            }
        }

        translate([-15, 0, 30]) {
            rotate([0, 0, 45]) {
                leaf();
            }
        }

        // Seed pattern lines
        for(i = [0:45:315]) {
            rotate([0, 0, i]) {
                translate([0, 0, 15]) {
                    difference() {
                        sphere(d = 40);
                        translate([0, 0, -5]) {
                            cylinder(h = 50, d = 38);
                        }
                    }
                }
            }
        }

        // Small seed bumps
        for(a = [0:60:300]) {
            rotate([0, 0, a]) {
                translate([18, 0, 20]) {
                    color(brown) {
                        sphere(d = 4);
                    }
                }
            }
        }
    }
}

// Render the model
sunkern();

