// Color definitions
color_body = [0.8, 0.9, 0.95];      // Light blue
color_belly = [0.9, 0.9, 0.85];     // Off-white
color_spikes = [0.7, 0.8, 0.9];     // Darker blue
color_details = [0.2, 0.2, 0.2];    // Black

// Overall scale
scale_factor = 2;

// Main body
module body() {
    color(color_body)
    union() {
        // Main torso
        translate([0, 0, 15])
            scale([1.2, 0.8, 1])
            sphere(15, $fn=32);

        // Belly
        color(color_belly)
        translate([0, -5, 12])
            scale([1.1, 0.6, 0.8])
            sphere(12, $fn=32);
    }
}

// Head
module head() {
    color(color_body)
    union() {
        // Main head shape
        translate([0, 0, 35])
            scale([1, 0.9, 0.8])
            sphere(12, $fn=32);

        // Mouth area
        translate([0, 12, 32])
            scale([0.7, 0.5, 0.5])
            sphere(8, $fn=32);
    }
}

// Eyes
module eyes() {
    // Left eye
    translate([-5, 10, 38])
    color([1, 1, 1])
    sphere(2.5, $fn=16);

    // Right eye
    translate([5, 10, 38])
    color([1, 1, 1])
    sphere(2.5, $fn=16);

    // Pupils
    translate([-5, 10.5, 38.5])
    color(color_details)
    sphere(1.2, $fn=16);

    translate([5, 10.5, 38.5])
    color(color_details)
    sphere(1.2, $fn=16);
}

// Nose
module nose() {
    translate([0, 14, 35])
    color(color_body)
    scale([1.5, 1, 1])
    sphere(2, $fn=16);
}

// Mouth
module mouth() {
    translate([0, 16.5, 32])
    color(color_details)
    rotate([90, 0, 0])
    cylinder(h=0.5, r1=3, r2=3, $fn=32);
}

// Ears
module ears() {
    // Left ear
    translate([-12, 5, 42])
    rotate([0, 15, -20])
    color(color_body)
    scale([0.8, 0.3, 2])
    sphere(4, $fn=16);

    // Right ear
    translate([12, 5, 42])
    rotate([0, 15, 20])
    color(color_body)
    scale([0.8, 0.3, 2])
    sphere(4, $fn=16);
}

// Arms
module arms() {
    // Left arm
    translate([-18, 0, 20])
    rotate([0, 0, -30])
    color(color_body)
    scale([0.6, 0.6, 1.2])
    sphere(6, $fn=16);

    // Right arm
    translate([18, 0, 20])
    rotate([0, 0, 30])
    color(color_body)
    scale([0.6, 0.6, 1.2])
    sphere(6, $fn=16);
}

// Legs
module legs() {
    // Left leg
    translate([-10, -10, 5])
    color(color_body)
    scale([0.7, 0.7, 1.5])
    sphere(6, $fn=16);

    // Right leg
    translate([10, -10, 5])
    color(color_body)
    scale([0.7, 0.7, 1.5])
    sphere(6, $fn=16);
}

// Tail
module tail() {
    color(color_body)
    union() {
        // Base of tail
        translate([0, -20, 15])
        rotate([0, 0, 0])
        scale([0.8, 1.5, 0.8])
        sphere(8, $fn=32);

        // Tail extension
        translate([0, -30, 12])
        rotate([0, 0, 0])
        scale([0.6, 1.2, 0.6])
        sphere(6, $fn=32);
    }
}

// Back spikes
module spikes() {
    color(color_spikes)
    for(i = [-1:1]) {
        translate([i * 8, -5, 28])
        rotate([0, 0, 0])
        scale([1, 0.5, 1.5])
        cylinder(h=3, r1=2, r2=1, $fn=16);
    }
}

// Assembly
scale(scale_factor)
union() {
    body();
    head();
    eyes();
    nose();
    mouth();
    ears();
    arms();
    legs();
    tail();
    spikes();
}

// Espeon - Medium complexity realistic style
// 3D printable model with proper wall thickness and no overhangs

// Colors
head_color = "#f4a6b8";
body_color = "#f596b8";
ear_color = "#e596c8";
tail_color = "#e596c8";
gem_color = "#ff69b4";
eye_color = "#ff1493";
marking_color = "#8b4c96";

// Scale factor for 3D printing (80mm tall)
scale_factor = 1;

// Main body
module espeon_body() {
    color(body_color) {
        // Torso - rounded body
        union() {
            // Main body sphere
            translate([0, 0, 10])
                scale([1.2, 1, 1.1])
                sphere(r=15, $fn=32);

            // Chest extension
            translate([0, 8, 8])
                scale([0.8, 0.6, 0.8])
                sphere(r=12, $fn=32);

            // Lower body
            translate([0, -2, 0])
                scale([0.9, 1, 1.2])
                sphere(r=14, $fn=32);
        }
    }
}

// Head with realistic features
module espeon_head() {
    color(head_color) {
        union() {
            // Main head shape
            translate([0, 20, 25])
                scale([1.1, 1.2, 1])
                sphere(r=18, $fn=32);

            // Snout
            translate([0, 30, 22])
                scale([0.6, 0.8, 0.5])
                sphere(r=12, $fn=32);

            // Forehead gem base
            translate([0, 15, 32])
                cylinder(h=3, r1=4, r2=5, $fn=16);
        }
    }
}

// Ears with split design
module espeon_ear(left_right) {
    color(ear_color) {
        translate([left_right * 12, 15, 35]) {
            rotate([0, left_right * -10, left_right * 15]) {
                union() {
                    // Main ear
                    scale([0.3, 0.1, 1.5])
                        sphere(r=20, $fn=16);

                    // Ear split - front part
                    translate([0, 0, 5])
                        rotate([left_right * 20, 0, 0])
                        scale([0.25, 0.08, 0.8])
                        sphere(r=15, $fn=16);

                    // Ear split - back part
                    translate([0, 0, -5])
                        rotate([left_right * -20, 0, 0])
                        scale([0.25, 0.08, 0.8])
                        sphere(r=15, $fn=16);
                }
            }
        }
    }
}

// Eyes with realistic shape
module espeon_eye(left_right) {
    color(eye_color) {
        translate([left_right * 8, 28, 25]) {
            // Eye socket
            difference() {
                scale([1.2, 1, 1])
                    sphere(r=5, $fn=16);

                // Pupil
                translate([0, 2, 1])
                    color("#330033")
                    scale([0.5, 0.7, 0.3])
                    sphere(r=4, $fn=16);
            }

            // Eye shine
            translate([left_right * 1, 29.5, 26.5])
                color("#ffffff")
                sphere(r=1, $fn=8);
        }
    }
}

// Tail split into multiple parts
module espeon_tail() {
    color(tail_color) {
        translate([0, -20, 15]) {
            rotate([0, 0, 0]) {
                // Tail base
                cylinder(h=8, r1=6, r2=4, $fn=16);

                // Tail segments
                for(i = [1:3]) {
                    rotate([0, 0, i * 15])
                        translate([0, -i * 5, -i * 2])
                            scale([1 - i * 0.15, 1 - i * 0.15, 1])
                            cylinder(h=10, r1=4 - i, r2=3 - i * 0.8, $fn=12);
                }

                // Tail fork
                translate([0, -20, -8]) {
                    // Left fork
                    rotate([0, 0, -30])
                        scale([0.6, 0.6, 1])
                        cylinder(h=15, r1=2.5, r2=1, $fn=8);

                    // Right fork
                    rotate([0, 0, 30])
                        scale([0.6, 0.6, 1])
                        cylinder(h=15, r1=2.5, r2=1, $fn=8);
                }
            }
        }
    }
}

// Legs with paws
module espeon_leg(pos_x, pos_y, pos_z, angle) {
    color(body_color) {
        translate([pos_x, pos_y, pos_z]) {
            rotate([0, 0, angle]) {
                // Upper leg
                cylinder(h=8, r1=3, r2=2.5, $fn=12);

                // Lower leg
                translate([0, 0, -8])
                    cylinder(h=8, r1=2.5, r2=2, $fn=12);

                // Paw
                translate([0, 0, -16])
                    scale([1.2, 1.2, 0.6])
                    sphere(r=3, $fn=16);
            }
        }
    }
}

// Body markings
module espeon_markings() {
    color(marking_color) {
        // Forehead gem
        translate([0, 15, 35])
            color(gem_color)
            sphere(r=2.5, $fn=16);

        // Leg markings
        for(i = [-1, 1]) {
            translate([i * 5, 5, 8])
                scale([0.3, 0.8, 0.1])
                rotate([90, 0, 0])
                cylinder(h=8, r1=3, r2=3, $fn=8);
        }
    }
}

// Main assembly
scale(scale_factor) {
    union() {
        espeon_body();
        espeon_head();
        espeon_ear(-1);  // Left ear
        espeon_ear(1);   // Right ear
        espeon_eye(-1);  // Left eye
        espeon_eye(1);   // Right eye
        espeon_tail();

        // Legs - positioned for stability
        espeon_leg(-8, 0, 0, 20);   // Front left
        espeon_leg(8, 0, 0, -20);   // Front right
        espeon_leg(-6, -15, 0, -20);  // Back left
        espeon_leg(6, -15, 0, 20);   // Back right

        espeon_markings();
    }
}

