$fn=50;

// Overall scale for 3D printing
scale_factor = 0.4;

// Colors
color_body = [0.05, 0.05, 0.05];
color_beak = [0.9, 0.5, 0.1];
color_eyes = [0.2, 0.2, 0.8];
color_wing = [0.08, 0.08, 0.08];
color_hat = [0.2, 0.1, 0.2];

module murkrow() {
    scale([scale_factor, scale_factor, scale_factor]) {
        union() {
            // Main body
            color(color_body) {
                translate([0, 0, 10]) {
                    sphere(d=30);
                }
                // Body extension
                translate([0, 0, 0]) {
                    sphere(d=25);
                }
            }

            // Head
            color(color_body) {
                translate([0, 20, 35]) {
                    sphere(d=20);
                }
            }

            // Beak
            color(color_beak) {
                translate([0, 28, 35]) {
                    rotate([0, -15, 0]) {
                        cylinder(h=12, d1=8, d2=2, center=true);
                    }
                }
            }

            // Eyes
            color(color_eyes) {
                translate([-6, 25, 38]) {
                    sphere(d=4);
                }
                translate([6, 25, 38]) {
                    sphere(d=4);
                }
            }

            // Eye whites
            color([1, 1, 1]) {
                translate([-6, 24.5, 39.5]) {
                    sphere(d=2.5);
                }
                translate([6, 24.5, 39.5]) {
                    sphere(d=2.5);
                }
            }

            // Hat brim
            color(color_hat) {
                translate([0, 15, 45]) {
                    rotate([90, 0, 0]) {
                        cylinder(h=2, d=25);
                    }
                }
            }

            // Hat top
            color(color_hat) {
                translate([0, 15, 47]) {
                    cylinder(h=8, d=15);
                }
                translate([0, 15, 55]) {
                    sphere(d=8);
                }
            }

            // Wings
            module wing() {
                color(color_wing) {
                    union() {
                        translate([0, 0, 0]) {
                            rotate([0, 90, 0]) {
                                cylinder(h=20, d=5, center=true);
                            }
                        }
                        translate([10, 0, 0]) {
                            rotate([0, 90, 0]) {
                                cylinder(h=15, d=5, center=true);
                            }
                        }
                        translate([18, 0, 0]) {
                            rotate([0, 90, 0]) {
                                cylinder(h=10, d=4, center=true);
                            }
                        }
                    }
                }
            }

            // Left wing
            translate([15, 5, 20]) {
                rotate([0, 0, 45]) {
                    wing();
                }
            }

            // Right wing
            translate([-15, 5, 20]) {
                rotate([0, 0, -45]) {
                    mirror([1, 0, 0]) {
                        wing();
                    }
                }
            }

            // Tail feathers
            module tail_feather() {
                color(color_body) {
                    translate([0, 0, 0]) {
                        rotate([0, 90, 0]) {
                            cylinder(h=15, d=3, center=true);
                        }
                    }
                }
            }

            // Three tail feathers
            translate([0, -15, 5]) {
                rotate([30, 0, 0]) {
                    tail_feather();
                }
            }
            translate([5, -15, 5]) {
                rotate([45, 0, 0]) {
                    tail_feather();
                }
            }
            translate([-5, -15, 5]) {
                rotate([15, 0, 0]) {
                    tail_feather();
                }
            }

            // Feet
            color(color_beak) {
                translate([5, -5, 0]) {
                    cylinder(h=5, d=3);
                }
                translate([-5, -5, 0]) {
                    cylinder(h=5, d=3);
                }
            }

            // Feet toes
            color(color_beak) {
                translate([7, -5, 0]) {
                    cylinder(h=2, d1=2, d2=1);
                }
                translate([3, -5, 0]) {
                    cylinder(h=2, d1=2, d2=1);
                }
                translate([-7, -5, 0]) {
                    cylinder(h=2, d1=2, d2=1);
                }
                translate([-3, -5, 0]) {
                    cylinder(h=2, d1=2, d2=1);
                }
            }
        }
    }
}

// Render the model
murkrow();

// Slowking Pokemon - Realistic Style
// Size: 80mm tall

// Color definitions
pink = [0.95, 0.75, 0.8];
white = [0.95, 0.95, 0.95];
black = [0.1, 0.1, 0.1];
yellow = [0.9, 0.8, 0.3];
red = [0.8, 0.2, 0.2];
shell = [0.85, 0.65, 0.5];

// Main module
module slowking() {
    union() {
        // Body
        color(pink) body();

        // Head and neck
        color(pink) head();

        // Shell on head
        color(shell) shell();

        // Arms
        color(pink) left_arm();
        color(pink) right_arm();

        // Legs
        color(pink) legs();

        // Tail
        color(pink) tail();
    }
}

// Body - oval shape
module body() {
    translate([0, 0, 25]) {
        scale([1.2, 1, 1.5]) sphere(15);
    }
}

// Head with neck
module head() {
    // Neck
    translate([0, 0, 45]) {
        cylinder(h=10, r1=8, r2=12);
    }

    // Head sphere
    translate([0, 0, 58]) {
        sphere(12);
    }

    // Snout
    translate([0, -10, 58]) {
        scale([0.8, 1.2, 0.8]) sphere(8);
    }

    // Eyes
    color(white) translate([-5, -8, 62]) sphere(3);
    color(white) translate([5, -8, 62]) sphere(3);
    color(black) translate([-5, -9.5, 63]) sphere(1.5);
    color(black) translate([5, -9.5, 63]) sphere(1.5);

    // Mouth
    color(black) translate([0, -12, 58]) {
        rotate([90, 0, 0]) cylinder(h=0.5, r=3, $fn=20);
    }
}

// Shell on head
module shell() {
    // Main shell
    translate([0, 0, 68]) {
        difference() {
            cylinder(h=8, r=16, $fn=6);
            translate([0, 0, -1]) cylinder(h=10, r=14, $fn=6);
        }
    }

    // Shell ridges
    for (i = [0:5]) {
        rotate([0, 0, i * 60]) {
            translate([0, 12, 72]) {
                cube([2, 4, 2], center=true);
            }
        }
    }

    // Gem in center
    color(red) translate([0, 0, 76.5]) {
        sphere(3);
    }
}

// Left arm
module left_arm() {
    // Shoulder
    translate([-18, 0, 40]) {
        sphere(5);
    }

    // Upper arm
    translate([-23, -5, 35]) {
        rotate([0, 45, -30]) {
            cylinder(h=15, r=4);
        }
    }

    // Hand
    translate([-30, -12, 30]) {
        sphere(4);
    }
}

// Right arm
module right_arm() {
    mirror([1, 0, 0]) left_arm();
}

// Legs
module legs() {
    // Left leg
    translate([-10, 0, 10]) {
        cylinder(h=20, r1=6, r2=5);
    }

    // Left foot
    translate([-10, -5, 0]) {
        scale([1.5, 2, 1]) sphere(5);
    }

    // Right leg
    translate([10, 0, 10]) {
        cylinder(h=20, r1=6, r2=5);
    }

    // Right foot
    translate([10, -5, 0]) {
        scale([1.5, 2, 1]) sphere(5);
    }
}

// Tail
module tail() {
    translate([0, 15, 20]) {
        rotate([0, -20, 0]) {
            cylinder(h=25, r1=6, r2=3);
        }
    }

    // Tail tip
    translate([0, 37, 15]) {
        sphere(4);
    }
}

// Generate the model
slowking();

// Misdreavus - Realistic Medium Complexity
// Overall height ~70mm

module misdreavus() {
    // Body base color
    color([0.4,0.2,0.6]) {

        // Main head sphere
        translate([0,0,35]) sphere(d=40, $fn=48);

        // Neck extension
        translate([0,0,15]) cylinder(d1=35,d2=25,h=20,$fn=32);

        // Bottom ruffle base
        difference() {
            translate([0,0,10]) sphere(d=45,$fn=48);
            translate([0,0,25]) sphere(d=40,$fn=48);
        }
    }

    // Eyes - red with yellow highlights
    color([0.9,0.1,0.1]) {
        for(side=[-1,1]) {
            translate([side*12,20,40]) sphere(d=12,$fn=24);
            color([1,0.9,0]) 
                translate([side*12,26,42]) sphere(d=4,$fn=16);
        }
    }

    // Mouth - black
    color([0.1,0.1,0.1]) {
        difference() {
            translate([0,18,32]) sphere(d=8,$fn=32);
            translate([0,25,32]) cube([12,12,12],center=true);
        }
    }

    // Pearl necklace
    for(i=[0:5]) {
        angle = i*60;
        color([0.9,0.9,0.95]) {
            translate([
                20*cos(angle),
                20*sin(angle),
                25
            ]) sphere(d=6,$fn=16);
        }
