        // Main spherical body
        sphere(r = body_r);

        // Bottom flat base
        translate([0, 0, -body_r])
            cylinder(h = body_h - body_r, r1 = body_r, r2 = body_r * 0.8, $fn = 32);
    }
}

// Hexagonal plates covering the body
module hex_plate(size, height) {
    color(highlight_color) {
        cylinder(h = height, r = size, $fn = 6);
    }
}

// Generate hexagonal armor plates
module armor_plates() {
    for (i = [0:5]) {
        rotate([0, 0, i * 60])
            translate([body_r * 0.8, 0, body_r * 0.3])
                rotate([90, 0, 0])
                    hex_plate(body_r * 0.4, plate_thickness);
    }

    // Additional plates on top hemisphere
    for (i = [0:11]) {
        rotate([0, 0, i * 30])
            translate([body_r * 0.6, 0, body_r * 0.7])
                rotate([90, 0, 0])
                    hex_plate(body_r * 0.25, plate_thickness * 0.8);
    }
}

// Eye sockets and eyes
module eyes() {
    // Left eye
    translate([-body_r * 0.4, body_r * 0.7, body_r * 0.3]) {
        color(dark_color)
            sphere(r = body_r * 0.15);
        color(eye_color)
            translate([body_r * 0.05, 0, 0])
                sphere(r = body_r * 0.08);
    }

    // Right eye
    translate([body_r * 0.4, body_r * 0.7, body_r * 0.3]) {
        color(dark_color)
            sphere(r = body_r * 0.15);
        color(eye_color)
            translate([-body_r * 0.05, 0, 0])
                sphere(r = body_r * 0.08);
    }
}

// Cannon openings (Forretress's signature feature)
module cannons() {
    for (i = [0:3]) {
        rotate([0, 0, i * 90])
            translate([body_r * 0.9, 0, body_r * 0.2]) {
                // Cannon barrel
                color(dark_color) {
                    rotate([0, 90, 0])
                        cylinder(h = body_r * 0.6, r = body_r * 0.15, $fn = 16);

                    // Recessed opening
                    translate([body_r * 0.6, 0, 0])
                        rotate([0, 90, 0])
                            cylinder(h = body_r * 0.3, r1 = body_r * 0.15, r2 = body_r * 0.1, $fn = 16);
                }
            }
    }
}

// Metallic ridges and details
