    // Top ridge
    color(highlight_color) {
        translate([0, 0, body_r * 0.9])
            cylinder(h = plate_thickness * 2, r1 = body_r * 0.3, r2 = body_r * 0.2, $fn = 32);
    }

    // Side vents
    for (i = [0:7]) {
        rotate([0, 0, i * 45])
