light_gray = [0.65,0.65,0.65];

module steelix() {
    // Body segments
    color(steel_gray) {
        // Head
        union() {
            translate([0,0,8]) {
                // Head cube
                cube([16,12,10], center=true);

                // Snout extension
                translate([8,0,0]) rotate([0,-15,0]) 
                    cube([10,8,6], center=true);

                // Jaw bottom
                translate([2,-6,2]) 
                    cube([12,6,4], center=true);
            }

            // Eye ridges
            translate([-4,5,13]) rotate([0,30,0])
                cube([6,4,3], center=true);
            translate([-4,-5,13]) rotate([0,-30,0])
                cube([6,4,3], center=true);

            // Eyes
            color(dark_gray) {
                translate([-7,4,12]) sphere(1.5);
                translate([-7,-4,12]) sphere(1.5);
            }
        }

        // Body segments - using loop for repetitive parts
        for(i=[1:8]) {
            translate([i*9,0,8-i*0.5]) {
                rotate([0,0,i*5]) {
                    // Main segment body
                    cube([10,8-i*0.3,8-i*0.3], center=true);

                    // Top spikes
                    translate([0,0,5-i*0.2]) 
                        cube([2,1,3], center=true);

                    // Side spikes
                    translate([0,4.5-i*0.2,0]) 
                        cube([1,2,2], center=true);
                    translate([0,-4.5+i*0.2,0]) 
                        cube([1,2,2], center=true);
                }
            }
        }

        // Tail end
        translate([76,0,3]) rotate([0,0,40]) 
            cube([12,6,4], center=true);
    }

    // Metallic details
    color(silver) {
        // Head ridges
        for(i=[0:2]) {
            translate([2+i*3,0,13]) 
                cube([2,10,1], center=true);
        }

        // Body plates
        for(i=[1:8]) {
            translate([i*9,0,8-i*0.5]) {
                // Hexagonal pattern (simulated with cubes)
                for(j=[-1:1]) {
                    for(k=[-1:1]) {
                        if(j!=0 || k!=0) {
                            translate([j*2,k*2,4-i*0.2]) 
                                cube([1.5,1.5,0.5], center=true);
                        }
                    }
                }
            }
        }
    }

    // Darker accent details
    color(dark_gray) {
        // Mouth opening
        translate([5,0,5]) 
            cube([6,4,2], center=true);

        // Segment joints
        for(i=[1:7]) {
            translate([i*9+4.5,0,8-i*0.5]) 
                cylinder(h=8-i*0.3, r1=3.5, r2=3.5, center=true);
        }
    }
}

