// Shell rock texture
module rock_spike(x, y, z, s) {
    color([0.5, 0.25, 0.15]) {
        translate([x, y, z]) {
            scale([s, s, s*1.5]) {
                sphere(2);
            }
        }
    }
}

// Place rock spikes on shell
for(i = [0:5]) {
    angle = i*60;
    rock_spike(cos(angle)*12, sin(angle)*12, 15, 1 + sin(i*30)*0.5);
}

