// Swinub - Ice/Ground type pig Pokemon
// Overall size approximately 60mm long

// Colors
$fn=32;

// Main body - brown oval shape
module body() {
    color("saddlebrown")
    union() {
        // Main torso
        scale([1.2, 0.8, 0.6])
            sphere(20);

        // Snout extension
        translate([22, 0, -2])
            scale([1.5, 1, 1])
                sphere(8);
    }
}

// Ice tusks
module tusk() {
    color("ivory")
    rotate([0, -20, 0])
        cylinder(h=12, r1=2, r2=0.5);
}

// Ears - rounded triangles
module ear() {
    color("saddlebrown")
    union() {
        // Main ear shape
        translate([0, 0, 5])
            rotate([90, 0, 0])
                cylinder(h=10, r1=8, r2=0);
        // Inner ear
        color("lightpink")
        translate([0, 0, 6])
            rotate([90, 0, 0])
                cylinder(h=8, r1=5, r2=0);
    }
}

// Eyes
module eye() {
    color("black")
    sphere(2);
}

// Nostrils
module nostril() {
    color("black")
    sphere(1.5);
}

// Legs
module leg() {
    color("saddlebrown")
    union() {
        // Upper leg
        cylinder(h=8, r=4);
        // Foot
        translate([0, 0, -8])
            scale([1.5, 2, 0.5])
                sphere(3);
    }
}

// Fur tufts
module fur_tuft() {
    color("saddlebrown")
    rotate([0, 90, 0])
        cylinder(h=5, r1=3, r2=0);
}

// Main assembly
translate([0, 0, 15]) {
    // Body
    body();

    // Head features
    translate([18, 0, 8]) {
        // Tusks
        translate([2, 5, -2]) tusk();
        translate([2, -5, -2]) tusk();

        // Eyes
        translate([2, 4, 2]) eye();
        translate([2, -4, 2]) eye();

        // Nostrils
        translate([6, 2, 0]) nostril();
        translate([6, -2, 0]) nostril();
    }

    // Ears
    translate([8, 12, 18]) rotate([0, -10, 20]) ear();
    translate([8, -12, 18]) rotate([0, -10, -20]) ear();

    // Legs
    translate([5, 8, -8]) leg();
    translate([5, -8, -8]) leg();
    translate([-10, 8, -8]) leg();
    translate([-10, -8, -8]) leg();

    // Back fur tufts
    for(i=[-20:5:-5]) {
        translate([i, 12, 5]) fur_tuft();
        translate([i, -12, 5]) fur_tuft();
    }

    // Top fur tufts
    for(i=[-10:5:15]) {
        translate([i, 0, 15]) fur_tuft();
    }
}

// Piloswine - Realistic Medium Complexity
// Overall dimensions: ~70mm long, ~50mm tall, ~45mm wide

$fn = 32;

// Main body color - brown
module piloswine() {
    color("#8B4513") {
        // Main body - large oval shape
        translate([0, 0, 15]) 
            scale([1.2, 0.9, 1]) 
                sphere(r = 20);

        // Belly - slightly lighter brown
        color("#A0522D") {
            translate([0, 12, 10]) 
                scale([1.4, 0.5, 0.8]) 
                    sphere(r = 18);
        }

        // Head section
        translate([-25, 0, 20]) 
            rotate([0, -10, 0]) 
                sphere(r = 16);

        // Snout
        color("#654321") {
            translate([-40, 0, 18]) 
                scale([1.5, 0.8, 0.8]) 
                    sphere(r = 10);
        }

        // Legs - four thick legs
        for(i = [-1, 1]) {
            // Front legs
            translate([-15, i * 15, 0]) {
                rotate([0, 0, i * 5]) {
                    cylinder(h = 15, r1 = 8, r2 = 6);
                    translate([0, 0, 15]) 
                        sphere(r = 7);
                }
            }

            // Rear legs
            translate([15, i * 15, 0]) {
                rotate([0, 0, -i * 5]) {
                    cylinder(h = 15, r1 = 9, r2 = 7);
                    translate([0, 0, 15]) 
                        sphere(r = 8);
                }
            }
        }

        // Tusks - two large curved tusks
        color("#FFF8DC") {
            for(side = [-1, 1]) {
                translate([-35, side * 8, 15]) {
                    rotate([0, side * 15, -20]) {
                        scale([0.3, 0.3, 1]) 
                            cylinder(h = 20, r1 = 6, r2 = 4);
                    }
                }
            }
        }

        // Fur tufts - using small spheres
        color("#D2B48C") {
            // Back fur
            for(i = [-20, -10, 0, 10]) {
                translate([i, 0, 35]) 
                    sphere(r = 6);
            }

            // Side fur
            for(angle = [30:60:330]) {
                translate([cos(angle) * 20, sin(angle) * 20, 25]) 
                    sphere(r = 4);
            }

            // Head fur
            translate([-30, 0, 30]) 
                sphere(r = 8);
        }

        // Eyes
        color("#000000") {
            translate([-35, 8, 25]) 
                sphere(r = 2);
            translate([-35, -8, 25]) 
                sphere(r = 2);
        }

        // Nose
        color("#000000") {
            translate([-45, 0, 18]) {
                sphere(r = 1.5);
                translate([0, 3, 0]) 
                    sphere(r = 1.5);
                translate([0, -3, 0]) 
                    sphere(r = 1.5);
            }
        }

        // Tail - short and stubby
        color("#8B4513") {
            translate([25, 0, 15]) {
                scale([1.2, 1, 0.8]) 
                    sphere(r = 10);
                color("#D2B48C") {
                    translate([5, 0, 5]) 
                        sphere(r = 6);
                }
            }
        }
    }
}

// Create the model
piloswine();

// Corsola Pokemon - Realistic Style, Medium Complexity
// Approximate size: 80mm height

// Coral color
coral = "#FF8A80";
pink = "#FFB6C1";

// Main body module
module corsola_body() {
    color(coral) {
        // Central body - rounded cylinder
        translate([0, 0, 15]) {
            scale([1, 0.9, 1.1]) 
                sphere(d=30, $fn=30);
        }

        // Bottom base
        translate([0, 0, 5]) {
            cylinder(h=10, d1=35, d2=30, $fn=30);
        }
    }
}

// Coral branch module
module coral_branch(h, r, angle, rot_z, tx=0, ty=0) {
    color(coral) {
        translate([tx, ty, 25]) {
            rotate([angle, 0, rot_z]) {
                // Main branch
                cylinder(h=h, r1=r, r2=r*0.7, $fn=8);

                // Branch tip
                translate([0, 0, h]) {
                    sphere(d=r*2, $fn=12);

                    // Small sub-branches
                    for(i=[0:120:359]) {
                        rotate([0, 0, i]) {
                            translate([r*0.8, 0, 0]) {
                                rotate([30, 0, 0]) {
                                    cylinder(h=r*1.5, r1=r*0.3, r2=r*0.2, $fn=6);
                                    translate([0, 0, r*1.5]) 
                                        sphere(d=r*0.6, $fn=8);
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Face details
module face() {
    // Eyes
    color("black") {
        translate([-7, 12, 35]) {
            sphere(d=4, $fn=12);
        }
        translate([7, 12, 35]) {
            sphere(d=4, $fn=12);
        }

        // Pupils
        color("white") {
            translate([-8, 13, 37]) {
                sphere(d=1.5, $fn=8);
            }
            translate([6, 13, 37]) {
                sphere(d=1.5, $fn=8);
            }
        }
    }

    // Mouth
    color("#8B4513") {
        translate([0, 3, 32]) {
            rotate([90, 0, 0]) {
                cylinder(h=1, d1=8, d2=5, $fn=16);
            }
        }
    }
}

// Coral markings
module markings() {
    color(pink) {
        // Circular spots on body
        for(i=[0:45:359]) {
            rotate([0, 0, i]) {
                translate([12, 0, 25]) {
                    sphere(d=4, $fn=10);
                }
            }
        }

        // Additional spots on branches
        translate([15, 10, 35]) sphere(d=3, $fn=8);
        translate([-15, -8, 38]) sphere(d=3, $fn=8);
        translate([8, -15, 40]) sphere(d=3, $fn=8);
    }
}

// Main assembly
union() {
    // Body
    corsola_body();

    // Main coral branches
    coral_branch(25, 4, 20, 45, 10, 15);
    coral_branch(20, 3.5, 30, 135, -12, 8);
    coral_branch(22, 4, 25, 225, -8, -10);
    coral_branch(18, 3, 35, 315, 12, -12);
    coral_branch(15, 3.5, 15, 0, 0, 18);
    coral_branch(16, 3, 10, 180, 0, -15);

    // Smaller branches
    coral_branch(10, 2, 45, 60, 18, 5);
    coral_branch(12, 2.5, 40, 240, -15, -5);

    // Face
    face();

    // Markings
    markings();
}

// Remoraid - Realistic Style, Medium Complexity
// Scale: ~80mm length

$fn = 32;
scale_factor = 1.5;

// Main body
module body() {
    color("lightslategray") {
        // Main fish body
        union() {
            // Torso
            translate([0, 0, 0]) 
                scale([2.5, 1.2, 1]) 
                    sphere(r = 8);

            // Tail section
            translate([-20, 0, 0]) 
                scale([1.5, 0.8, 0.8]) 
                    sphere(r = 6);

            // Snout
            translate([15, 0, 0]) 
                scale([2, 0.6, 0.6]) 
                    cylinder(h = 12, r1 = 4, r2 = 2);
        }
    }
}

// Dorsal fin
module dorsal_fin() {
    color("dimgrey") {
        translate([0, 0, 8]) {
            rotate([90, 0, 0]) {
                linear_extrude(height = 1) {
                    polygon(points = [[-5, 0], [0, 8], [5, 0]]);
                }
            }
        }
    }
}

// Tail fin
module tail_fin() {
    color("dimgrey") {
        translate([-25, 0, 0]) {
            rotate([90, 0, 0]) {
                // Top lobe
                translate([0, 0, 3]) 
                    scale([1, 1.5, 1]) 
                        rotate([0, 0, 20]) 
                            cylinder(h = 1, r1 = 5, r2 = 0);

                // Bottom lobe
                translate([0, 0, -3]) 
                    scale([1, 1.5, 1]) 
                        rotate([0, 0, -20]) 
                            cylinder(h = 1, r1 = 5, r2 = 0);
            }
        }
    }
}

// Pectoral fins
module pectoral_fins() {
    color("dimgrey") {
        for(side = [-1, 1]) {
            translate([0, side * 8, -3]) {
                rotate([0, side * 30, 0]) {
                    scale([1.5, 1, 0.3]) 
                        rotate([0, 90, 0]) 
                            cylinder(h = 6, r1 = 3, r2 = 0);
                }
            }
        }
    }
}

// Eye
module eye() {
    color("black") {
        translate([12, -4, 3]) 
            sphere(r = 2);

        // Eye highlight
        color("white") {
            translate([13, -3.5, 3.5]) 
                sphere(r = 0.5);
        }
    }
}

// Mouth
module mouth() {
    color("black") {
        translate([18, 0, 0]) {
            rotate([90, 0, 0]) {
                difference() {
                    cylinder(h = 0.5, r = 2);
                    translate([0, 0, -0.25]) 
                        cube([4, 2, 1], center = true);
                }
            }
        }
    }
}

// Body markings
module markings() {
    color("darkslategray") {
        // Stripe pattern
        for(i = [-1, 0, 1]) {
            translate([i * 5, 0, 7.5]) {
                rotate([90, 0, 0]) 
                    cylinder(h = 0.5, r = 2);
            }
        }

        // Spot patterns
        for(pos = [[5, 5, 0], [2, 6, 2], [-3, 5, -1]]) {
            translate(pos) 
                sphere(r = 1);
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        body();
        dorsal_fin();
        tail_fin();
        pectoral_fins();
        eye();
        mirror([0, 1, 0]) eye();
        mouth();
        markings();
    }
}

// Octillery Pokemon - Realistic Style, Medium Complexity
// Size: ~80mm tall

// Main colors
$fn = 32;
body_color = [0.2, 0.4, 0.8];
mouth_color = [0.6, 0.2, 0.2];
eye_color = [1, 1, 0];
black = [0, 0, 0];
yellow = [1, 0.8, 0];

// Body
module octillery_body() {
    color(body_color)
    union() {
        // Main body - teardrop shape
        translate([0, 0, 20])
        scale([1, 1.2, 1.5])
        sphere(25);

        // Snout extension
        translate([0, 30, 15])
        rotate([30, 0, 0])
        scale([0.8, 1, 1.2])
        cylinder(20, 15, 10);

        // Head bump
        translate([0, -5, 35])
        sphere(10);
    }
}

// Eyes
module eyes() {
    // Left eye
    translate([-12, 25, 35])
    union() {
        color(eye_color)
        sphere(6);
        color(black)
        translate([0, 0, 5.5])
        sphere(3);
    }

    // Right eye
    translate([12, 25, 35])
    union() {
        color(eye_color)
        sphere(6);
        color(black)
        translate([0, 0, 5.5])
        sphere(3);
    }
}

// Mouth
module mouth() {
    color(mouth_color)
    translate([0, 40, 15])
    rotate([90, 0, 0])
    cylinder(5, 12, 8);
}

// Tentacles
module tentacle(length, angle) {
    color(body_color)
    rotate([0, angle, 0])
    translate([0, 0, -length/2])
    scale([1, 1, length/10])
    sphere(5);
}

// Array of tentacles
module tentacles() {
    for (i = [0:45:315]) {
        rotate([0, 0, i])
        translate([20, 0, 0])
        tentacle(30, 30);
    }
}

// Yellow suction cups
module suction_cups() {
    color(yellow)
    for (i = [0:45:315]) {
        rotate([0, 0, i])
        translate([20, 0, -10])
        scale([1.5, 1.5, 0.5])
        sphere(5);
    }
}

// Top fin
module top_fin() {
    color(body_color)
    translate([0, -15, 45])
    rotate([0, 0, 0])
    scale([1, 0.3, 0.5])
    cylinder(15, 2, 15);
}

// Side fins
module side_fins() {
    // Left fin
    color(body_color)
    translate([-25, 0, 20])
    rotate([0, 90, 0])
    scale([0.5, 1, 1])
    cylinder(15, 5, 2);

    // Right fin
    translate([25, 0, 20])
    rotate([0, 90, 0])
    scale([0.5, 1, 1])
    cylinder(15, 5, 2);
}

// Assembly
union() {
    octillery_body();
    eyes();
    mouth();
    tentacles();
    suction_cups();
    top_fin();
    side_fins();
}

