color_mantine_markings = "#1E90FF";   // Dodger blue
color_eyes = "#000000";
color_mouth = "#2F4F4F";

// Scale factor for 3D printing (80mm wingspan)
scale_factor = 1;

// Main body module
module mantine_body() {
    color(color_mantine_body) {
        // Central body - oval shape
        translate([0, 0, 5]) {
            scale([1.2, 0.8, 0.6]) sphere(15);
        }

        // Body underside
        color(color_mantine_underside) {
            translate([0, 0, 2]) {
                scale([1.1, 0.7, 0.3]) sphere(14);
            }
        }
    }
}

// Wing module with ribbing
module wing(side) {
    mirror([side, 0, 0]) {
        color(color_mantine_body) {
            // Main wing fin
            translate([-35, 0, 8]) {
                rotate([0, 0, -10]) {
                    scale([2.5, 0.3, 1.2]) sphere(10);
                }
