module porygon2() {
    // Main body - hexagonal prism
    color([0.9, 0.2, 0.6]) {
        difference() {
            cylinder(h=30, r=25, $fn=6);
            translate([0,0,-1]) cylinder(h=32, r=23, $fn=6);
        }
    }

    // Head extension - tapered cylinder
    color([0.9, 0.2, 0.6]) {
        translate([0, 0, 31]) cylinder(h1=20, h2=15, r1=25, r2=20, $fn=6);
    }

    // Face panel - blue hexagon
    color([0.2, 0.5, 0.9]) {
        translate([0, 0, 15]) {
            difference() {
                cylinder(h=2, r=22, $fn=6);
                translate([0,0,-1]) cylinder(h=4, r=20, $fn=6);
            }
        }
    }

    // Eyes - 3D spheres
    color([1,1,1]) {
        // Left eye
        translate([-10, 12, 18]) sphere(r=5);
        // Right eye
        translate([10, 12, 18]) sphere(r=5);
    }

    // Eye pupils
    color([0,0,0]) {
        translate([-10, 14, 20]) sphere(r=2);
        translate([10, 14, 20]) sphere(r=2);
    }

    // Beak - triangular prism
    color([0.9, 0.9, 0.1]) {
        translate([0, 22, 16]) {
            rotate([90,0,0]) {
                linear_extrude(height=8) {
                    polygon(points=[[-5,0], [5,0], [0,10]]);
                }
            }
        }
    }

    // Neck ring
    color([0.9, 0.2, 0.6]) {
        translate([0,0,30]) {
            difference() {
                cylinder(h=3, r=28, $fn=20);
                cylinder(h=3.5, r=26, $fn=20);
            }
        }
    }

    // Legs - simple cylinders
    module leg() {
        color([0.9, 0.2, 0.6]) {
            difference() {
                cylinder(h=15, r=5, $fn=12);
                translate([0,0,-1]) cylinder(h=17, r=4, $fn=12);
            }
        }
    }

    // Left leg
    translate([-15, -12, -15]) rotate([10,0,0]) leg();
    // Right leg
    translate([15, -12, -15]) rotate([10,0,0]) leg();

    // Feet - flat boxes
    color([0.9, 0.2, 0.6]) {
        // Left foot
        translate([-15, -18, -22]) cube([10, 12, 3], center=true);
        // Right foot
        translate([15, -18, -22]) cube([10, 12, 3], center=true);
    }

    // Arms - tapered cylinders
    module arm() {
        color([0.9, 0.2, 0.6]) {
            rotate([0,90,0]) cylinder(h=20, r1=4, r2=3, $fn=8);
        }
    }

    // Left arm
    translate([-25, 0, 0]) rotate([0,0,-30]) arm();
    // Right arm
    translate([25, 0, 0]) rotate([0,0,30]) mirror([0,1,0]) arm();

    // Tail segments
    module tail_segment(len, rad) {
        color([0.9, 0.2, 0.6]) {
            rotate([0,90,0]) cylinder(h=len, r=rad, $fn=6);
        }
    }

    // Tail connection
    translate([0, -25, 0]) tail_segment(15, 5);
    translate([0, -35, 0]) rotate([0,0,45]) tail_segment(12, 4);
    translate([-8, -42, 0]) rotate([0,0,90]) tail_segment(10, 3);

    // Wing modules
    module wing() {
        color([0.9, 0.2, 0.6]) {
            difference() {
                translate([0,0,0]) cube([20, 2, 25]);
                translate([0,1,5]) cube([16, 2, 15]);
            }
        }
    }

    // Left wing
    translate([-25, 0, 15]) rotate([0,0,60]) wing();
    // Right wing
    translate([25, 0, 15]) rotate([0,0,-60]) mirror([1,0,0]) wing();

    // Detailing lines on body
    color([0.7, 0.1, 0.5]) {
        for(i=[-2:2]) {
            translate([i*8, 0, 16]) {
                rotate([90,0,0]) cylinder(h=0.5, r=0.5, $fn=6);
                translate([0, 15, 0]) rotate([90,0,0]) cylinder(h=0.5, r=0.5, $fn=6);
            }
        }
    }
}

