color_red = [0.9, 0.2, 0.2];
color_white = [1, 1, 1];

// Scale factor
scale_factor = 0.6;

module elekid() {
    scale([scale_factor, scale_factor, scale_factor]) {

        // Body (yellow oval)
        color(color_yellow)
        translate([0, 0, 30])
        scale([1.2, 1, 1.5])
        sphere(d = 40);

        // Head
        color(color_yellow)
        translate([0, 0, 55])
        scale([1.3, 1.1, 1.1])
