        color(color_black)
        difference() {
            translate([0, 18, 55])
            scale([1.35, 0.3, 0.2])
            sphere(d = 30);

            translate([0, 0, 0])
            sphere(d = 50);
        }

        // Eyes
        module eye(x, y, z) {
            color(color_white)
            translate([x, y, z])
            sphere(d = 8);

            color(color_black)
            translate([x, y - 2, z + 2])
            sphere(d = 5);
        }

        eye(-10, 18, 55);
        eye(10, 18, 55);

        // Mouth
        color(color_black)
        translate([0, 25, 45])
        rotate([90, 0, 0])
        cylinder(h = 1, d1 = 12, d2 = 8);

        // Ears
        module ear(x) {
            color(color_yellow)
            translate([x * 18, 5, 65])
            rotate([x * -15, 0, 0])
            scale([0.4, 0.3, 1.2])
            cylinder(h = 25, d1 = 12, d2 = 8);

            color(color_black)
            translate([x * 23, 5, 75])
            rotate([x * -15, 0, 0])
            scale([0.3, 0.2, 1])
            cylinder(h = 15, d1 = 8, d2 = 5);
        }

        ear(-1);
        ear(1);

        // Arms
        module arm(x) {
            color(color_yellow)
            translate([x * 25, 0, 40])
            rotate([0, 0, x * 20])
            scale([0.4, 0.4, 1.2])
            cylinder(h = 20, d = 15);

            // Hands
            color(color_yellow)
            translate([x * 35, 0, 28])
            sphere(d = 18);

            // Fingers
            for(i = [0:2]) {
                color(color_yellow)
                translate([x * 38, (i - 1) * 5, 25])
                rotate([0, x * 20, 0])
                cylinder(h = 8, d = 4);
            }
        }

        arm(-1);
        arm(1);

        // Legs
        module leg(x) {
            color(color_yellow)
            translate([x * 12, 0, 15])
            scale([0.5, 0.5, 1])
            cylinder(h = 20, d = 15);

            // Feet
            color(color_black)
            translate([x * 12, 5, 5])
            scale([1.2, 1.5, 0.8])
            sphere(d = 15);
        }

        leg(-1);
        leg(1);

        // Tail (lightning bolt shape)
        color(color_yellow)
        translate([-25, -5, 25])
        rotate([0, 0, 45])
        linear_extrude(height = 5)
        polygon(points=[
            [0, 0], [5, 10], [2, 10], [7, 20], [4, 20], [10, 35],
            [0, 25], [3, 25], [-2, 15], [1, 15], [-5, 0]
        ]);

        // Belly pattern
        color(color_black)
        difference() {
            translate([0, -20, 45])
            scale([1, 0.8, 1])
            sphere(d = 20);

            translate([0, -25, 45])
            scale([0.8, 0.6, 1.1])
            sphere(d = 20);
        }

        // Antennae
        color(color_yellow)
        translate([0, 0, 75])
        cylinder(h = 8, d = 4);

        color(color_red)
        translate([0, 0, 83])
        sphere(d = 6);
