            translate([side * 15, -8, -25]) {
                cylinder(h1=15, h2=12, d1=12, d2=15, $fn=12);

                // Feet - flattened spheres
                translate([0, 0, -8])
                    scale([1, 1.2, 0.5]) sphere(d=18, $fn=15);
