            rotate([0, 90, 0])
                cylinder(h = 5 * scale_factor, r = 8 * scale_factor, center = true);
    }

    // Spike details
    color("gray")
    for (angle = [0:45:315]) {
        rotate([0, 0, angle])
            translate([35 * scale_factor, 0, 20 * scale_factor])
                scale([1, 0.6, 1])
                    cylinder(h = 12 * scale_factor, r1 = 8 * scale_factor, r2 = 0);
    }

    // Side spikes
    for (angle = [30:120:240]) {
        rotate([0, 0, angle])
            translate([0, 38 * scale_factor, 10 * scale_factor])
                scale([0.6, 1, 1])
                    cylinder(h = 15 * scale_factor, r1 = 7 * scale_factor, r2 = 0);
    }

    // Back spikes
    color("gray")
    for (angle = [0:60:300]) {
        rotate([0, 0, angle])
            translate([0, -38 * scale_factor, 10 * scale_factor])
                scale([0.6, 1, 1])
                    cylinder(h = 15 * scale_factor, r1 = 7 * scale_factor, r2 = 0);
    }

    // Bottom vent
    color("darkgray")
    translate([0, 0, -45 * scale_factor])
        cylinder(h = 5 * scale_factor, r = 25 * scale_factor);

    // Shell cracks
    color("black")
    for (i = [1:4]) {
        rotate([0, 0, i * 90])
            translate([0, 0, 0])
                scale([1, 0.8, 1])
                    rotate([90, 0, 0])
                        linear_extrude(height = 2 * scale_factor)
                            polygon(points = [[30, -5], [35, 0], [30, 5]]);
    }

    // Top horn
