    translate([0, 0, 55 * scale_factor])
        rotate([0, 0, 0])
            cylinder(h = 20 * scale_factor, r1 = 5 * scale_factor, r2 = 0);

    // Secondary horns
    for (x = [-20, 20]) {
        color("gray")
        translate([x * scale_factor, 5 * scale_factor, 45 * scale_factor])
            rotate([0, 20 * (x > 0 ? 1 : -1), 0])
                cylinder(h = 15 * scale_factor, r1 = 4 * scale_factor, r2 = 0);
    }
}

