// Swampert OpenSCAD model - medium complexity realistic style
// Size: ~60mm height, 3D printable

// Main colors
$fn=30;

module swampert() {
    color([0.2, 0.4, 0.7]) // Main blue body

    // Body
    union() {
        // Torso
        translate([0, 0, 15])
            scale([1.2, 0.8, 1.4])
            sphere(r=15);

        // Head
        translate([0, 10, 35])
            scale([0.9, 0.7, 0.9])
            sphere(r=15);

        // Legs
        translate([-10, -5, 0]) left_leg();
        translate([10, -5, 0]) mirror([1,0,0]) left_leg();

        // Arms
        translate([-20, 5, 25]) left_arm();
        translate([20, 5, 25]) mirror([1,0,0]) left_arm();

        // Tail
        translate([0, -25, 10]) tail();

        // Orange accents
        color([0.9, 0.4, 0.1]) {
            // Head fins
            translate([0, 15, 45]) scale([2, 0.5, 1]) rotate([0, 45, 0]) cube([10,2,8]);
            translate([-8, 15, 42]) rotate([0, 30, -30]) scale([0.3,1,1]) cube([15,2,8]);
            translate([8, 15, 42]) rotate([0, 30, 30]) scale([0.3,1,1]) cube([15,2,8]);

            // Back ridges
            for(i=[0:2]) {
                translate([0, -5-i*8, 30-i*3])
                    scale([1-i*0.1, 0.3, 1-i*0.1])
                    rotate([0, 15, 0])
                    cube([15,3,5]);
            }

            // Cheek fins
            translate([-12, 8, 30]) rotate([0, 0, -45]) cube([8,2,6]);
            translate([12, 8, 30]) rotate([0, 0, 45]) cube([8,2,6]);
        }

        // Eyes
        color([0.9, 0.9, 0.9]) {
            translate([-5, 12, 38]) sphere(r=2);
            translate([5, 12, 38]) sphere(r=2);
        }

        color([0.1, 0.1, 0.1]) {
            translate([-5, 13, 39]) sphere(r=1.2);
            translate([5, 13, 39]) sphere(r=1.2);
        }

        // Mouth
        color([0.5, 0.2, 0.3])
            translate([0, 8, 32])
            difference() {
                cube([10,2,3], center=true);
                translate([0,-1,1]) cube([8,2,3], center=true);
            }
    }
}

module left_leg() {
    union() {
        // Upper leg
        translate([0, 0, 10])
            scale([0.8, 0.8, 1.2])
