        translate([0, 35, 12])
            scale([0.8, 1.5, 0.6])
            sphere(r=8);
    }

    // Ears
    module ear(x_offset) {
        color(body_color) {
            translate([x_offset, 20, 25])
                rotate([0, -20, x_offset > 0 ? -20 : 20])
                scale([0.3, 1.5, 1.2])
                cylinder(h=15, r1=5, r2=2);

