            color(accent_color)
            translate([x_offset, 20, 25])
                rotate([0, -20, x_offset > 0 ? -20 : 20])
                scale([0.2, 1.2, 1])
                cylinder(h=13, r1=3, r2=1);
        }
    }

    ear(8);
    ear(-8);

    // Eyes
    module eye(x_offset) {
        color(eye_color) {
            translate([x_offset, 30, 18])
                rotate([0, 20, 0])
                scale([0.8, 1, 0.5])
                sphere(r=3);

            // Pupil
            color([0, 0, 0])
            translate([x_offset + 1, 30, 19])
                sphere(r=1.5);
        }
    }

    eye(5);
    eye(-5);

    // Nose
    color(nose_color)
    translate([0, 42, 12])
        sphere(r=2);

    // Mouth line
    color(nose_color)
    translate([0, 40, 10])
        rotate([0, 90, 0])
        cylinder(h=8, r=0.5);

    // Legs
    module leg(x_offset, y_offset) {
        color(body_color) {
            translate([x_offset, y_offset, -10])
                scale([0.8, 0.8, 1.5])
                cylinder(h=20, r=6);

            // Paw
            translate([x_offset, y_offset, -25])
                sphere(r=7);

            // Toes
            for(i = [-1:1]) {
                translate([x_offset + i*3, y_offset, -28])
                    sphere(r=3);
            }
        }
    }

    leg(8, 5);
    leg(-8, 5);
    leg(6, -10);
    leg(-6, -10);

    // Tail
    color(body_color) {
        translate([0, -20, 0])
            rotate([0, 0, 10])
            scale([0.8, 0.8, 2])
            cylinder(h=25, r1=8, r2=3);

        // Tail tip
        translate([0, -35, 5])
            rotate([0, 0, 45])
            scale([0.6, 0.6, 1])
            sphere(r=5);
    }

    // Mane
    color(accent_color) {
        // Neck mane
        translate([0, 15, 8])
            rotate([0, 0, 0])
            scale([1.5, 1, 0.5])
            cylinder(h=20, r=15);

        // Back stripe
        translate([0, 0, 12])
            scale([0.8, 2, 0.3])
            cube([15, 30, 3], center=true);

        // Tail stripe
        translate([0, -30, 8])
            scale([0.5, 0.3, 0.5])
            cube([8, 20, 5], center=true);
    }

    // Paws color accent
    color(accent_color) {
        leg_paw(8, 5);
        leg_paw(-8, 5);
        leg_paw(6, -10);
        leg_paw(-6, -10);
    }
}

module leg_paw(x_offset, y_offset) {
